INSERT INTO questions (topic_id, text, options, correct_index, explanation) VALUES
('general_knowledge', 'What is the most important reason for doing a pre-trip inspection?', '["To make sure nothing has been stolen","Safety for yourself and other road users","Because other drivers do it","To make sure vehicle will reach destination"]'::jsonb, 1, 'Safety is the most important reason - for yourself and other road users.'),
('general_knowledge', 'What are the important parts in the steering system?', '["Main spring, axle, spring shackle","Rim, lug nuts, tire","Leaf spring, vehicle frame, torque rod","Tie rod, spindle, pitman arm"]'::jsonb, 3, 'Tie rod, spindle, and pitman arm are key steering system components.'),
('general_knowledge', 'How many red reflective triangles should you carry?', '["Seven","Five","One","Three"]'::jsonb, 3, 'You must carry three red reflective triangles for emergencies.'),
('general_knowledge', 'Why keep the starter switch key in your pocket during pre-trip inspection?', '["So no one moves the vehicle while you inspect underneath","To open the side box","t lose it","To inspect the trailer"]'::jsonb, 0, 'Prevents someone from starting the vehicle while you''re underneath.'),
('general_knowledge', 'Proper procedure to start moving on a hill without rolling back?', '["Leave trailer brakes on and drag tires","Partly engage clutch before releasing brake","Increase RPM and release clutch quickly","Put blocks behind tires"]'::jsonb, 1, 'Partly engaging the clutch before releasing the brake prevents rollback.'),
('general_knowledge', 'When the road is slippery, retarders will help keep you from skidding.', '["True","False"]'::jsonb, 1, 'False. Retarders can cause skidding on slippery roads.'),
('general_knowledge', 'How far ahead should you look while driving?', '["15-20 seconds","12-15 seconds","5-8 seconds","9-12 seconds"]'::jsonb, 1, 'Look 12-15 seconds ahead (about a quarter mile at highway speeds).'),
('general_knowledge', 'On a divided highway, where should reflectors be placed?', '["10, 100, and 200 feet toward traffic","20, 150, and 300 feet","15, 75, and 150 feet","30, 100, and 150 feet"]'::jsonb, 0, 'Place at 10, 100, and 200 feet toward approaching traffic.'),
('general_knowledge', 'Minimum tread depth for tires other than front tires?', '["3/32 inch","1/32 inch","4/32 inch","2/32 inch"]'::jsonb, 3, 'Non-steering tires need 2/32 inch. Front tires need 4/32 inch.'),
('general_knowledge', 'Two main things to look ahead for while driving?', '["Road signs and detours","Traffic and road conditions","Fire and flooding","Truck stops and police"]'::jsonb, 1, 'Scan ahead for traffic and road conditions to anticipate hazards.'),
('general_knowledge', 'If you double your speed, stopping distance increases by?', '["Two times","Three times","Four times","Five times"]'::jsonb, 2, 'Stopping distance increases by four times when you double speed.'),
('general_knowledge', 'Empty trucks have the best braking because they are lighter.', '["True","False"]'::jsonb, 1, 'False. Empty trucks have poorer braking due to less traction.'),
('general_knowledge', 'Which are suspension system defects?', '["Broken leaves in leaf spring","Cracked spring hangers","All of these","Leaking shock absorbers"]'::jsonb, 2, 'All of these are suspension system defects to check during inspection.'),
('general_knowledge', 'Three types of emergency equipment required?', '["Flares, fire extinguisher, spare fuses","First aid kit, fire extinguisher, flares","Warning devices, first aid kit, flares","Fire extinguisher, spare fuses, warning devices"]'::jsonb, 3, 'Fire extinguisher, spare electrical fuses, and warning devices are required.'),
('general_knowledge', 'How to test hydraulic brakes for leaks?', '["Pump 5 times, hold 3 seconds","Hold down 5 seconds and listen","Move slowly forward and brake hard","Pump 3 times, hold 5 seconds - pedal should not move"]'::jsonb, 3, 'Pump three times, apply firm pressure for 5 seconds. Pedal should not move.'),
('general_knowledge', 'When should you downshift?', '["After an accident","Before entering a curve and before going down a hill","Only at the bottom of hills","Only on flat roads"]'::jsonb, 1, 'Downshift before entering curves and before starting down hills.'),
('general_knowledge', 'How do you know when to shift?', '["Engine speed and road speed","Every 3-5 seconds","Shift indicator light only","Road speed and wind speed"]'::jsonb, 0, 'Use engine speed (RPM) and road speed to determine shift timing.'),
('general_knowledge', 'It is best to use low beams whenever you can.', '["True","False"]'::jsonb, 1, 'False. High beams let you see farther and should be used when safe.'),
('general_knowledge', 'You can safely remove radiator cap if engine isn''t overheated.', '["True","False"]'::jsonb, 1, 'False. Cooling system stays pressurized and hot. Always let it cool.'),
('general_knowledge', 'If tire blows out, put brakes on hard and stop quickly.', '["True","False"]'::jsonb, 1, 'False. Stay off brakes, hold wheel firmly, slow down gradually.'),
('general_knowledge', 'Two causes of tire fires?', '["Under-inflated tires and poor retreads","Under-inflated tires and duals that touch","Duals that touch and road debris","Hot brakes and under-inflated tires"]'::jsonb, 1, 'Under-inflated tires and dual tires touching cause heat and friction fires.'),
('general_knowledge', 'When using fire extinguisher, get as close as possible to fire.', '["True","False"]'::jsonb, 1, 'False. Stay back to avoid injury and let agent spread effectively.'),
('general_knowledge', 'Coffee and fresh air will help a drinker sober up.', '["True","False"]'::jsonb, 1, 'False. Only time reduces blood alcohol content.'),
('general_knowledge', 'Minimum tie downs for any flatbed load?', '["Four","One","Six","Two"]'::jsonb, 3, 'At least two tie downs required for any flatbed load.'),
('general_knowledge', 'What to check on front of vehicle during walk-around?', '["All lights: low beams, high beams, flashers, signals","Only headlights","Only tires","Nothing specific"]'::jsonb, 0, 'Check all lights work: low beams, high beams, flashers, turn signals.'),
('general_knowledge', 'Why are placards used?', '["It is the law","Communicate the risk","Prevent mixing with food","Prevent terrorism"]'::jsonb, 1, 'Placards communicate cargo risk to emergency responders and drivers.'),
('general_knowledge', 'If you become sleepy while driving, you should?', '["Turn up the radio","Roll down windows","Stop to sleep","Drink caffeinated beverages"]'::jsonb, 2, 'The only safe solution is to stop and sleep.'),
('general_knowledge', 'When do you downshift automatic transmissions?', '["Before going down a hill","Never","At the bottom of a hill","Halfway down"]'::jsonb, 0, 'Downshift automatics before going down a hill, not during descent.'),
('general_knowledge', 'Best way to see sides and rear of your vehicle?', '["Roll window down","A helper","Your mirrors","Rear cameras only"]'::jsonb, 2, 'Properly adjusted mirrors are the best way to monitor sides and rear.'),
('general_knowledge', 'In safe driving, communicating means?', '["Using turn signals, flashers, and horn","Using hand signals","Using CB radio","Yelling out window"]'::jsonb, 0, 'Communicate using turn signals, four-way flashers, and horn.'),
('general_knowledge', 'If followed too closely, decrease your following distance.', '["True","False"]'::jsonb, 1, 'False. Increase following distance for more stopping space.'),
('general_knowledge', 'Swinging wide left before turning right may cause others to pass on right.', '["True","False"]'::jsonb, 0, 'True. Others may think you''re going left and try to pass on right.'),
('general_knowledge', 'If drowsy, what should you do before driving?', '["Get sleep","Exercise","Drink coffee","Cold shower"]'::jsonb, 0, 'Getting adequate sleep is the only effective solution.'),
('general_knowledge', 'Wet brakes can cause?', '["Brakes to apply unevenly","Lack of braking power","All of these","Pulling to one side"]'::jsonb, 2, 'Wet brakes cause uneven braking, lack of power, and pulling.'),
('general_knowledge', 'To avoid effects of wet brakes?', '["Light brake pressure while crossing water","Light pressure after to dry brakes","Slow down before water","All of these"]'::jsonb, 3, 'All these methods help prevent or mitigate wet brake effects.'),
('general_knowledge', 'Factors determining safe speed on steep downgrade?', '["Weight only","Weight, steepness, weather","Length and steepness only","Weight, length, steepness, road conditions, weather"]'::jsonb, 3, 'All factors: weight, length, steepness, conditions, and weather.'),
('general_knowledge', 'Why be in proper gear before starting down a hill?', '["Same gear needed at bottom","Not necessary","s the law","Cannot shift to lower gear once going down"]'::jsonb, 3, 'Once going downhill, you may not be able to downshift.'),
('general_knowledge', 'What is a hazard?', '["Any road condition or user that is possible danger","Only hazardous materials","Only equipment failure","Only weather conditions"]'::jsonb, 0, 'A hazard is any road condition or user that could be dangerous.'),
('general_knowledge', 'Advantage of going right instead of left around obstacle?', '["t force others into opposing traffic","Shoulder may be paved","Someone could be passing on left","All of these"]'::jsonb, 3, 'Going right avoids oncoming traffic, may use shoulder, avoids left passers.'),
('general_knowledge', 'At accident scene, to prevent another accident you should?', '["Move vehicle to side if involved","Park away if stopping to help","Use flashers and set out triangles","All of these"]'::jsonb, 3, 'All actions help prevent additional accidents.'),
('general_knowledge', 'Three cargo responsibilities for drivers?', '["Inspecting, recognizing overloads, ensuring properly secured","Inspecting, hauling, securing","Weighing, securing, delivering","Loading, weighing, unloading"]'::jsonb, 0, 'Drivers must inspect, recognize overloads, ensure proper securing.'),
('general_knowledge', 'A hazardous materials placard is?', '["Sign inside vehicle","Sign on back only","Sign outside identifying hazard class and weight","Sign outside identifying hazard class"]'::jsonb, 3, 'Placard is sign on outside identifying hazard class of cargo.'),
('general_knowledge', 'Which statement is FALSE about vehicle weights?', '["GCWR is max specified by manufacturer","GCW includes trailer and cargo","GCW is only powered unit plus trailers without cargo"]'::jsonb, 2, 'GCW includes cargo. It''s powered unit, trailers, AND cargo.'),
('general_knowledge', 'Before transporting sealed load, check that?', '["Placard matches seal numbers","Seal is authentic","Seal is secure","Load is properly secured"]'::jsonb, 3, 'Even with sealed loads, ensure cargo is properly secured.'),
('general_knowledge', 'Method to find seconds of following distance?', '["Divide speed by four","Divide speed by four, double at night","Count until you reach same spot, multiply by 2","Count seconds until you reach same spot vehicle ahead passed"]'::jsonb, 3, 'Count seconds from when vehicle ahead passes landmark until you reach it.'),
('general_knowledge', 'Why make emergency plans when you see a hazard?', '["All hazards become emergencies","Less time to act","Plan to communicate risk","More time to act if you see hazards before emergencies"]'::jsonb, 3, 'Seeing hazards early gives more time to react if they become emergencies.'),
('general_knowledge', 'What is a pull-up?', '["Pulling off shoulder until straight","DOT physical exercise","Pulling forward while backing to reposition trailer","Pulling into dock"]'::jsonb, 2, 'A pull-up is pulling forward while backing to reposition trailer.'),
('general_knowledge', 'Procedure: Brake to slow 5 mph below safe speed, release, repeat - used for?', '["Slippery roads","Long steep grades","Blown tire","All of these"]'::jsonb, 1, 'This controlled braking method is for long, steep downgrades.'),
('general_knowledge', 'Hydroplaning is?', '["Planning alternate route in rain","Tires lose contact with road due to water","Traction on slippery surfaces","Water spill on road"]'::jsonb, 1, 'Hydroplaning is when tires lose road contact, riding on water.'),
('general_knowledge', 'What is an escape ramp?', '["Ramp around accidents","Another term for off-ramp","Ramp with loose material to slow runaway vehicles","Rest area entrance"]'::jsonb, 2, 'Escape ramps have loose material (gravel/sand) to slow runaway vehicles.'),
('general_knowledge', 'Safe speed on curves depends on?', '["Posted speed only","Weight, height, and speed","Time of day","None of these"]'::jsonb, 1, 'Weight, height of load, and speed all affect how safely you can take curves.'),
('general_knowledge', 'At 55 mph, how many feet does a heavy vehicle travel per second?', '["About 40 feet","About 60 feet","About 80 feet","About 100 feet"]'::jsonb, 2, 'At 55 mph, you travel about 80 feet per second.'),
('general_knowledge', 'You should use low gear to slow down?', '["Only in emergencies","When going down a long steep grade","Only when brakes fail","Never"]'::jsonb, 1, 'Use low gear when going down long, steep grades to control speed.'),
('general_knowledge', 'If wheels start to spin, you should?', '["Apply more gas","Take foot off accelerator","Shift to higher gear","Pump the brakes"]'::jsonb, 1, 'Take foot off accelerator to regain traction.'),
('general_knowledge', 'Overloading can affect?', '["Steering","Braking","Speed control","All of these"]'::jsonb, 3, 'Overloading affects steering, braking, and speed control.'),
('general_knowledge', 'Gross Vehicle Weight Rating is?', '["Actual weight of vehicle and cargo","Maximum safe weight specified by manufacturer","Weight without cargo","Weight including passengers only"]'::jsonb, 1, 'GVWR is the maximum safe weight specified by manufacturer.'),
('general_knowledge', 'When loading cargo, heaviest items should be?', '["At the top","At the bottom","Wherever they fit","At the back"]'::jsonb, 1, 'Load heaviest items at bottom to keep center of gravity low.'),
('general_knowledge', 'Header boards?', '["Protect driver from cargo","Improve aerodynamics","Hold lights","Are decorative"]'::jsonb, 0, 'Header boards protect the driver from cargo moving forward.'),
('general_knowledge', 'If your vehicle starts to hydroplane, you should?', '["Speed up","Brake hard","Release accelerator and push in clutch","Turn sharply"]'::jsonb, 2, 'Release accelerator and push in clutch to regain traction.'),
('general_knowledge', 'What effect does cargo have on stopping?', '["No effect","More cargo means longer stopping distance","More cargo means shorter stopping distance","Only affects uphill stops"]'::jsonb, 1, 'More cargo means more weight, requiring longer stopping distance.'),
('general_knowledge', 'Bridges usually freeze?', '["Before other road surfaces","After other road surfaces","Never freeze","Only at night"]'::jsonb, 0, 'Bridges freeze before other surfaces because cold air circulates above and below.'),
('general_knowledge', 'Which of these is NOT a sign of drowsy driving?', '["Drifting from lane","Missing signs or exits","Sharp focus on road","Yawning repeatedly"]'::jsonb, 2, 'Sharp focus is a sign of alertness, not drowsiness.'),
('general_knowledge', 'How often should you check your mirrors?', '["Every 5-8 seconds","Every 10-15 seconds","Every minute","Only when changing lanes"]'::jsonb, 0, 'Check mirrors every 5-8 seconds to stay aware of surroundings.'),
('general_knowledge', 'Front-end header boards protect driver from?', '["Cold","Sun","Cargo crashing forward","Rear-end collisions"]'::jsonb, 2, 'Header boards protect driver from cargo crashing forward during sudden stops.'),
('general_knowledge', 'Total stopping distance is made up of?', '["Perception + reaction distance only","Reaction + braking distance only","Perception + reaction + braking distance","Braking distance only"]'::jsonb, 2, 'Total stopping distance = perception + reaction + braking distance.'),
('general_knowledge', 'If cargo is not properly secured, what can happen?', '["It can shift during transit","It can damage the vehicle","It can cause an accident","All of these"]'::jsonb, 3, 'Unsecured cargo can shift, damage vehicle, and cause accidents.'),
('general_knowledge', 'The best way to take a curve safely is to?', '["Speed up in the curve","Slow before entering, accelerate through","Brake hard in the curve","Coast through"]'::jsonb, 1, 'Slow down before entering the curve, then accelerate slightly through it.'),
('general_knowledge', 'When backing, you should?', '["Use only mirrors","Back quickly to get it over with","Get out and look before backing","Never get out of the cab"]'::jsonb, 2, 'Always get out and look at your path before backing.'),
('general_knowledge', 'Perception distance is?', '["Time to recognize hazard","Distance traveled while recognizing hazard","Distance traveled while braking","Time to stop"]'::jsonb, 1, 'Perception distance is distance traveled while recognizing a hazard.'),
('general_knowledge', 'When should you not use cruise control?', '["On straight highways","On wet roads","During the day","When alone"]'::jsonb, 1, 'Do not use cruise control on wet, icy, or slippery roads.'),
('general_knowledge', 'Large trucks take longer to stop because?', '["They have smaller brakes","They weigh more","They have fewer tires","They are older"]'::jsonb, 1, 'Greater weight requires more distance to stop.'),
('general_knowledge', 'When traveling at highway speeds, you need ___ of space ahead.', '["At least 4 seconds","At least 7 seconds","At least 2 seconds","At least 10 seconds"]'::jsonb, 1, 'At highway speeds, you need at least 7 seconds of space ahead.'),
('general_knowledge', 'If you brake too hard on wet roads you can?', '["Increase traction","Lock wheels and skid","Stop faster","Improve control"]'::jsonb, 1, 'Hard braking on wet roads can lock wheels and cause skidding.'),
('general_knowledge', 'You should increase following distance when?', '["Following motorcycles","In bad weather","When being tailgated","All of these"]'::jsonb, 3, 'Increase following distance in all these situations.'),
('general_knowledge', 'What is counter-steering?', '["Steering in opposite direction of a skid","Steering with opposite hand","Steering while braking","Steering uphill"]'::jsonb, 0, 'Counter-steering is turning into a skid to regain control.'),
('general_knowledge', 'Road surfaces most slippery when?', '["When wet for hours","Shortly after rain begins","When completely dry","In direct sunlight"]'::jsonb, 1, 'Roads are most slippery shortly after rain begins, before oil washes away.'),
('general_knowledge', 'To stay alert during long trips, you should?', '["Drive without breaks","Take breaks every 2 hours or 100 miles","Drink alcohol in moderation","Drive faster"]'::jsonb, 1, 'Take regular breaks every 2 hours or 100 miles to stay alert.'),
('general_knowledge', 'Legal blood alcohol concentration limit for CDL drivers?', '["0.08%","0.04%","0.10%","0.02%"]'::jsonb, 1, 'CDL drivers limit is 0.04%, half the regular limit.'),
('general_knowledge', 'You should signal lane changes at least ___ feet ahead.', '["25 feet","50 feet","100 feet","200 feet"]'::jsonb, 2, 'Signal lane changes at least 100 feet ahead.'),
('general_knowledge', 'Speed affects space ahead, but not space on the sides.', '["True","False"]'::jsonb, 1, 'False. Speed affects all space management, including sides.'),
('general_knowledge', 'When following another vehicle, look for their ___ to see road conditions ahead.', '["Mirrors","Brake lights","Tires","Tail lights"]'::jsonb, 2, 'Watch the tires of vehicles ahead to see road conditions and hazards.'),
('general_knowledge', '"Brake fade" means brakes are?', '["Working well","Getting weaker from overheating","Too cold","Too new"]'::jsonb, 1, 'Brake fade is loss of braking power from overheating.'),
('general_knowledge', 'You need more stopping distance on?', '["Warm, dry roads","Wet roads","New pavement","Straight roads"]'::jsonb, 1, 'Wet roads require more stopping distance due to reduced traction.'),
('general_knowledge', 'When should you avoid using your horn?', '["Near hospitals and schools at night","In emergencies","When passing","When signaling intent"]'::jsonb, 0, 'Avoid horn near hospitals and schools, especially at night.'),
('general_knowledge', 'Cargo should be rechecked after first ___ miles?', '["10 miles","25 miles","50 miles","100 miles"]'::jsonb, 1, 'Recheck cargo after first 25 miles, then periodically.'),
('general_knowledge', 'Which vehicle is most affected by side winds?', '["Low, heavy truck","Empty box van","Tanker truck","Flatbed with heavy load"]'::jsonb, 1, 'Empty box vans are most affected by side winds due to large surface area and light weight.'),
('general_knowledge', 'Night driving is more dangerous because?', '["Most people drive slower","You cannot see as far ahead","Roads are better lit","There is less traffic"]'::jsonb, 1, 'Night driving is dangerous because you cannot see as far ahead.'),
('general_knowledge', 'When driving in fog, use?', '["High beams","Low beams","No lights","Hazard lights only"]'::jsonb, 1, 'Use low beams in fog. High beams reflect back and reduce visibility.'),
('general_knowledge', 'While driving, you should keep your hands?', '["At bottom of steering wheel","clock","At top of wheel","On your lap when cruising"]'::jsonb, 1, 'Keep hands at 9 and 3 or 10 and 2 for best control.'),
('general_knowledge', 'Traffic safety is affected by?', '["Time pressure","Overconfidence","Distractions","All of these"]'::jsonb, 3, 'All of these factors negatively affect traffic safety.'),
('general_knowledge', 'Black ice usually appears on?', '["Sunny roads","Bridges and overpasses","Gravel roads","Desert highways"]'::jsonb, 1, 'Black ice forms on bridges and overpasses first due to cold air circulation.'),
('general_knowledge', 'You can drive next to other vehicles when?', '["Never - always have an escape route","Only in heavy traffic","Whenever necessary","On multi-lane highways"]'::jsonb, 0, 'Avoid driving next to others - always have an escape route.'),
('general_knowledge', 'Safe negotiating speed for a curve is?', '["Posted speed limit","Below posted speed","Above posted speed","Whatever feels right"]'::jsonb, 1, 'Safe curve speed for trucks is below posted limit (which is set for cars).'),
('general_knowledge', 'Aggressive driving does not include?', '["Tailgating","Speeding","Following at safe distance","Running red lights"]'::jsonb, 2, 'Following at a safe distance is safe driving, not aggressive.'),
('general_knowledge', 'When visibility is reduced, your speed should be?', '["Increased","Reduced","Same as always","Maximum posted"]'::jsonb, 1, 'Reduce speed when visibility is reduced.'),
('general_knowledge', 'Fatigue affects?', '["Vision","Judgment","Reaction time","All of these"]'::jsonb, 3, 'Fatigue affects vision, judgment, reaction time, and more.'),
('general_knowledge', 'The most common type of crash is?', '["Head-on","Rear-end","Rollover","Side-swipe"]'::jsonb, 1, 'Rear-end collisions are the most common crash type.'),
('general_knowledge', 'Merge lanes are used to?', '["Park vehicles","Allow vehicles to speed up before entering traffic","Slow down traffic","Make U-turns"]'::jsonb, 1, 'Merge lanes let you speed up to match traffic before merging.'),
('general_knowledge', 'When passing, you should?', '["Flash high beams at night","Pass on the right","Assume the other driver sees you","Pass slowly"]'::jsonb, 0, 'Flash high beams at night to signal passing (if appropriate).'),
('general_knowledge', 'If you must stop on a highway, pull off?', '["To the left shoulder","To the right shoulder as far as safely possible","In either direction","Remain in the lane"]'::jsonb, 1, 'Pull off to the right shoulder as far as safely possible.'),
('general_knowledge', 'If your brakes fail going downhill?', '["Pump brakes rapidly","Look for escape ramp","Shift to neutral","Turn off engine"]'::jsonb, 1, 'Look for an escape ramp - they are designed for brake failures.'),
('general_knowledge', 'In a front tire blowout, your steering will?', '["Stay the same","Pull to the side of the blowout","Become easier","Lock up"]'::jsonb, 1, 'Steering pulls toward the side with the blowout.'),
('general_knowledge', 'The best way to put out a tire fire is?', '["Water","Sand","Driving on it","CO2 or dry chemical extinguisher"]'::jsonb, 3, 'Use CO2 or dry chemical extinguisher, not water.'),
('general_knowledge', 'During fire, you should never?', '["Call for help","Open hood while fighting engine fire","Use fire extinguisher","Pull over"]'::jsonb, 1, 'Opening hood provides oxygen that can make fire worse.'),
('general_knowledge', 'If you must leave the road to avoid collision, try to?', '["Turn sharply","Keep one set of wheels on pavement","Apply brakes hard","Accelerate"]'::jsonb, 1, 'Keeping wheels on pavement helps maintain control.'),
('general_knowledge', 'If wheels skid, you should?', '["Accelerate","Brake harder","Steer in direction you want to go","Turn wheel opposite direction"]'::jsonb, 2, 'Steer in direction you want to go (into the skid).'),
('general_knowledge', 'Which type of fire is most dangerous?', '["Tire fire","Cargo fire","Engine fire","All can be dangerous"]'::jsonb, 3, 'All fires are dangerous and should be dealt with immediately.'),
('general_knowledge', 'B:C type fire extinguisher is for?', '["Cloth fires","Electrical and grease fires","Wood fires","Paper fires"]'::jsonb, 1, 'B:C extinguishers are for electrical and flammable liquid fires.'),
('general_knowledge', 'ABS helps you?', '["Stop faster","Maintain steering control during hard braking","Go faster","Save fuel"]'::jsonb, 1, 'ABS helps maintain steering control during hard braking.'),
('general_knowledge', 'When driving through deep water, test brakes by?', '["Waiting until dry road","Applying light pressure while driving slowly","Hard braking","Not testing"]'::jsonb, 1, 'Apply light brake pressure while driving slowly to dry brakes.'),
('general_knowledge', 'What is "off-tracking"?', '["Going off course","Rear wheels following different path than front","Losing cargo","Mechanical failure"]'::jsonb, 1, 'Off-tracking is when rear wheels follow a different path than front.'),
('general_knowledge', 'Oversized loads require?', '["Nothing special","Special permits","Extra fuel","Faster speeds"]'::jsonb, 1, 'Oversized loads require special permits and may need escorts.'),
('general_knowledge', 'Dry bulk tanks have a high center of gravity and?', '["Are easy to control","Are prone to rollover","Have best braking","Are lightweight"]'::jsonb, 1, 'Dry bulk tanks have high center of gravity, making rollover more likely.'),
('general_knowledge', 'You must secure cargo that could?', '["Shift","Fall off","Become hazard","All of these"]'::jsonb, 3, 'Secure cargo that could shift, fall, or become a hazard.'),
('general_knowledge', 'Aggregate working load limit must be at least?', '["Half cargo weight","Equal to cargo weight","1.5 times cargo weight","Double cargo weight"]'::jsonb, 0, 'Total working load of tie-downs must be at least half the cargo weight.'),
('general_knowledge', 'Which cargo requires special handling?', '["Metal coils","Meat carcasses","Building materials","All of the above"]'::jsonb, 3, 'All these require special handling and securement methods.'),
('general_knowledge', 'Coils should be secured with?', '["One chain","Banding steel","Proper blocking and bracing","Nothing special"]'::jsonb, 2, 'Metal coils need proper blocking, bracing, and tie-downs.'),
('general_knowledge', 'How much space should be between cargo?', '["As much as possible","None - pack tightly against header board","Maximum 1 inch","Only near doors"]'::jsonb, 1, 'Pack cargo tightly against header board to prevent shifting.'),
('general_knowledge', 'Weight should be distributed?', '["Front heavy","Rear heavy","Low and centered","At top"]'::jsonb, 2, 'Distribute weight low and centered for stability.'),
('general_knowledge', 'Tarps are used to?', '["Improve aerodynamics only","Protect cargo from weather","Look professional","Reduce weight"]'::jsonb, 1, 'Tarps protect cargo from weather and debris.'),
('general_knowledge', 'If cargo shifts while driving, you should?', '["Keep driving","Pull over safely and recheck","Speed up","Ignore it"]'::jsonb, 1, 'Pull over safely and recheck cargo if you suspect shifting.'),
('general_knowledge', 'A driver''s inspection of cargo is required?', '["Before driving","Within first 25 miles","Every 3 hours or 150 miles","All of these"]'::jsonb, 3, 'Check cargo before driving, within 25 miles, and every 3 hours/150 miles.'),
('general_knowledge', 'Loose cargo on flatbed can?', '["Blow off and cause accidents","Shift and cause rollover","Damage cargo","All of these"]'::jsonb, 3, 'Loose cargo can blow off, shift, and be damaged.'),
('general_knowledge', 'Bracing is used to?', '["Fill gaps between cargo","Prevent forward movement","Prevent side-to-side movement","All of these"]'::jsonb, 3, 'Bracing fills gaps and prevents cargo movement in all directions.'),
('general_knowledge', 'When transporting hazardous materials, you must stop?', '["At railroad crossings","At all intersections","Only at red lights","At weigh stations only"]'::jsonb, 0, 'Hazmat vehicles must stop at railroad crossings.'),
('general_knowledge', 'Sealed loads means you don''t need to check?', '["That count is correct","That it is properly secured","Weight limits","The seal"]'::jsonb, 0, 'You can''t check count on sealed loads, but must verify securing and weight.'),
('general_knowledge', 'What documents must accompany shipment?', '["Bill of lading only","Shipping papers","Cargo manifest","Appropriate shipping documents"]'::jsonb, 3, 'Shipments must have appropriate shipping documents.'),
('general_knowledge', 'When must you inspect cargo?', '["Every 25 miles","At every stop","At every change of driver","Every 150 miles or 3 hours"]'::jsonb, 3, 'Inspect cargo every 150 miles or 3 hours of driving.'),
('general_knowledge', 'Blocking is used to?', '["Increase cargo height","Keep cargo from moving","Reduce weight","Improve visibility"]'::jsonb, 1, 'Blocking keeps cargo from moving on the trailer.'),
('general_knowledge', 'Container chassis are used for?', '["Hauling loose cargo","Transporting shipping containers","General freight","Livestock"]'::jsonb, 1, 'Container chassis are specialized trailers for shipping containers.'),
('general_knowledge', 'A vehicle fire can spread quickly. You should?', '["Keep driving","Pull over, notify authorities, use extinguisher if safe","Wait for help","Pour water on it"]'::jsonb, 1, 'Pull over immediately, call for help, and fight fire if it''s safe.'),
('general_knowledge', 'Crash or accident reports must be submitted within?', '["1 day","10 days","24 hours","Varies by state"]'::jsonb, 3, 'Reporting requirements vary by state and type of accident.'),
('general_knowledge', 'When pulled over by law enforcement?', '["Exit vehicle immediately","Stay in cab with hands visible","Hide documents","Argue your case"]'::jsonb, 1, 'Stay in cab with hands visible until instructed otherwise.'),
('general_knowledge', 'If you have an accident with injuries?', '["Leave scene immediately","Help injured, call 911, secure scene","Move injured to roadside","Wait in vehicle"]'::jsonb, 1, 'Help injured if safe, call 911, secure the scene.'),
('general_knowledge', 'Controlled braking means?', '["Using cruise control","Applying brakes as hard as possible without locking","Pumping brakes","Using parking brake"]'::jsonb, 1, 'Controlled braking applies brakes hard without locking wheels.'),
('general_knowledge', 'Stab braking is?', '["Braking in curves","Apply brakes hard, release, repeat","Light constant pressure","Using ABS"]'::jsonb, 1, 'Stab braking: apply full brakes, release when wheels lock, repeat.'),
('general_knowledge', 'You should NOT use stab braking on vehicles with?', '["Air brakes","Hydraulic brakes","ABS","Manual transmission"]'::jsonb, 2, 'ABS does the pumping automatically; just apply steady pressure.'),
('general_knowledge', 'If ABS malfunction light is on?', '["t work","You still have regular brakes","Pull over immediately","Ignore it"]'::jsonb, 1, 'You still have regular brakes; ABS just won''t activate.'),
('general_knowledge', 'Emergency triangles must be placed within?', '["5 minutes","10 minutes","15 minutes","30 minutes"]'::jsonb, 1, 'Place warning devices within 10 minutes of stopping.'),
('general_knowledge', 'On a two-lane road, triangles should be placed?', '["All behind vehicle","100 feet front and back, one at vehicle","Only in front","Wherever convenient"]'::jsonb, 1, 'Place one 100ft ahead, one at vehicle, one 100ft behind.'),
('general_knowledge', 'If tire catches fire, you should?', '["Drive to water source","Stop and stay away","Keep driving to put it out","Ignore it"]'::jsonb, 1, 'Stop away from buildings, call for help - tires can explode.'),
('general_knowledge', 'Engine compartment fire fighting: Never?', '["Use extinguisher","Call for help","Open hood wide","Pull over first"]'::jsonb, 2, 'Don''t open hood wide - air feeds the fire.'),
('general_knowledge', 'If vehicle ahead stops suddenly, basic reaction is to?', '["Steer left","Brake hard","Stop and steer right if clear","Accelerate around"]'::jsonb, 2, 'Brake, and steer right if clear to avoid rear collision.'),
('general_knowledge', 'A cargo fire is dangerous because?', '["May contain hazardous materials","Fire may spread","Cargo may explode","All of these"]'::jsonb, 3, 'Cargo fires are dangerous for all these reasons.'),
('general_knowledge', 'To help prevent rollover you should?', '["Keep cargo as high as possible","Drive slowly around curves","Load heavy on one side","Overload slightly"]'::jsonb, 1, 'Take curves slowly - rollovers often occur due to speed.'),
('general_knowledge', 'A rearward amplification effect ("crack-the-whip") is worst in?', '["Single trailers","Doubles/triples","Straight trucks","Tankers"]'::jsonb, 1, 'The last trailer in doubles/triples has most whip effect.'),
('general_knowledge', 'If steering fails, first thing to do is?', '["Brake hard","Downshift and brake gently","Turn off engine","Jump out"]'::jsonb, 1, 'Downshift to slow down, use brakes gently.'),
('general_knowledge', 'Hazard perception is helped by?', '["Looking far ahead","Scanning mirrors","Identifying potential hazards","All of these"]'::jsonb, 3, 'All these help identify hazards early.'),
('general_knowledge', 'When approaching railroad crossing you should?', '["Speed up to cross quickly","Slow down, look, and listen","Only stop if lights are flashing","Use horn"]'::jsonb, 1, 'Slow down, look both ways, and listen.'),
('general_knowledge', 'A moving vehicle has?', '["Potential energy only","Kinetic energy","No energy","Chemical energy"]'::jsonb, 1, 'A moving vehicle has kinetic energy (energy of motion).'),
('general_knowledge', 'Maximum driving time before required break (property-carrying)?', '["8 hours","11 hours","14 hours","10 hours"]'::jsonb, 1, '11 hours maximum after 10 consecutive hours off duty.'),
('general_knowledge', 'After 8 hours driving, you need a break of at least?', '["15 minutes","30 minutes","45 minutes","1 hour"]'::jsonb, 1, '30-minute break required after 8 hours of driving.'),
('general_knowledge', 'Maximum hours on duty in 7 days (property)?', '["50 hours","60 hours","70 hours","80 hours"]'::jsonb, 1, '60 hours in 7 consecutive days for property carriers.'),
('general_knowledge', '34-hour restart restores your hours to?', '["Full weekly limit","Half weekly limit","Previous level","Nothing"]'::jsonb, 0, '34-hour restart restores your full 60/70 hour limit.'),
('general_knowledge', 'ELD stands for?', '["Electronic Log Device","Electronic Logging Device","Emergency Lighting Device","Engine Load Display"]'::jsonb, 1, 'Electronic Logging Device - required for most carriers.'),
('general_knowledge', 'Who must have an ELD?', '["All commercial vehicles","Most drivers who keep records of duty status","Only hazmat drivers","Only long-haul"]'::jsonb, 1, 'Most drivers required to keep RODS must use ELDs.'),
('general_knowledge', 'You can drive after 10 consecutive hours off duty.', '["True","False"]'::jsonb, 0, 'True. 10 consecutive hours off duty required before driving.'),
('general_knowledge', 'Commercial zones are exempt from some HOS rules.', '["True","False"]'::jsonb, 0, 'True. Short-haul and commercial zone exemptions exist.'),
('general_knowledge', 'Falsifying logs can result in?', '["Warning only","Fines and disqualification","Nothing","Extra hours"]'::jsonb, 1, 'Falsifying logs can result in heavy fines and license disqualification.'),
('general_knowledge', 'What does CDL stand for?', '["s License","Certified Driving License","Commercial Driving License","Class D License"]'::jsonb, 0, 'Commercial Driver''s License.'),
('general_knowledge', 'To drive a combination vehicle over 26,001 lbs you need?', '["Class C","Class B","Class A","No CDL needed"]'::jsonb, 2, 'Class A CDL for combination vehicles over 26,001 lbs GCWR.'),
('general_knowledge', 'Class B CDL is required for?', '["Any single vehicle over 26,001 lbs","Motorcycles","Any vehicle","Trailers only"]'::jsonb, 0, 'Class B for single vehicles over 26,001 lbs GVWR.'),
('general_knowledge', 'You must notify your employer of traffic violations within?', '["10 days","30 days","60 days","90 days"]'::jsonb, 1, 'Notify employer within 30 days of conviction.'),
('general_knowledge', 'Driving CMV while disqualified results in?', '["Warning","CDL revocation for 1 year minimum","Small fine","Nothing"]'::jsonb, 1, 'Driving while disqualified = 1 year minimum revocation.'),
('general_knowledge', 'CDL medical certificate is valid for?', '["1 year","2 years","3 years","5 years"]'::jsonb, 1, '2 years for most drivers (less for some conditions).'),
('general_knowledge', 'First offense DUI in CMV = disqualification for?', '["6 months","1 year","3 years","Lifetime"]'::jsonb, 1, '1 year disqualification for first DUI offense in CMV.'),
('general_knowledge', 'Second serious traffic violation in 3 years = disqualification for?', '["30 days","60 days","120 days","1 year"]'::jsonb, 1, '60-day disqualification for second serious violation.'),
('general_knowledge', 'Using a hand-held cell phone while driving a CMV is?', '["Allowed in emergencies only","Always prohibited","Allowed on highways","Allowed at slow speeds"]'::jsonb, 1, 'Hand-held phone use while driving CMV is prohibited.'),
('general_knowledge', 'Texting while driving a CMV results in?', '["Warning","Disqualification up to 120 days","Nothing","Small fine only"]'::jsonb, 1, 'Texting while driving can disqualify you up to 120 days.'),
('general_knowledge', 'Pre-employment drug testing is required for CDL drivers?', '["True","False"]'::jsonb, 0, 'True. Drug testing required before employment.'),
('general_knowledge', 'Random drug testing is done at what rate annually?', '["10%","25%","50%","Varies by regulation"]'::jsonb, 3, 'Rates set by DOT - vary by year and substance tested.'),
('general_knowledge', 'Refusing a drug or alcohol test is treated as?', '["No issue","Positive result","Minor violation","Allowed once"]'::jsonb, 1, 'Refusal is treated as a positive test result.'),
('general_knowledge', 'Post-accident testing is required when?', '["Always","When driver receives citation and there are injuries/fatality","Never","Only for hazmat"]'::jsonb, 1, 'Required when driver gets citation and injuries/fatality involved.'),
('general_knowledge', 'You cannot drive if BAC is?', '["0.08% or more","0.04% or more","0.02% or more","Any amount"]'::jsonb, 1, 'CDL drivers cannot drive with BAC of 0.04% or more.'),
('general_knowledge', 'How long must you wait after consuming alcohol?', '["2 hours","4 hours","Until BAC is below .04%","No waiting required"]'::jsonb, 1, 'Cannot drive within 4 hours of consuming alcohol.'),
('general_knowledge', 'What is an out-of-service order?', '["Order to stop for rest","Order to stop driving due to violation","Order to change routes","Order for maintenance"]'::jsonb, 1, 'Out-of-service order stops you from driving due to violation.'),
('general_knowledge', 'Violation of out-of-service order first offense?', '["Warning","1 year disqualification","180 days","Nothing"]'::jsonb, 1, '1 year disqualification for first out-of-service violation.'),
('general_knowledge', 'FMCSA regulates?', '["Airlines","Commercial motor vehicles","Trains","Ships"]'::jsonb, 1, 'Federal Motor Carrier Safety Administration regulates CMVs.'),
('general_knowledge', 'Driver Vehicle Inspection Report is required?', '["Daily after driving","Weekly","Monthly","Only after accidents"]'::jsonb, 0, 'DVIR required at end of each day of driving.'),
('general_knowledge', 'Who is responsible for vehicle safety?', '["Only the carrier","Only the driver","Both carrier and driver","Only the shipper"]'::jsonb, 2, 'Both carrier and driver share responsibility for safety.'),
('general_knowledge', 'Vehicle inspection must be done before each trip.', '["True","False"]'::jsonb, 0, 'True. Pre-trip inspection required before each trip.'),
('general_knowledge', 'Maximum allowed weight for most interstate roads?', '["60,000 lbs","80,000 lbs","100,000 lbs","Unlimited"]'::jsonb, 1, '80,000 lbs maximum for most interstate highways.'),
('general_knowledge', 'Axle weight limits typically are?', '["10,000 lbs","20,000 lbs steering, 34,000 lbs tandem","50,000 lbs","Unlimited"]'::jsonb, 1, '20,000 lbs steering axle, 34,000 lbs tandem axles typically.'),
('general_knowledge', 'Overweight permits allow you to exceed limits if?', '["Anyone can get them","Special circumstances and approved","With CDL automatically","Never"]'::jsonb, 1, 'Overweight permits granted for special circumstances only.'),
('general_knowledge', 'What endorsement is needed for tank vehicles?', '["H","N","P","T"]'::jsonb, 3, 'T endorsement for tank vehicles.'),
('general_knowledge', 'What endorsement is needed for hazardous materials?', '["H","N","P","T"]'::jsonb, 0, 'H endorsement for hazardous materials.'),
('general_knowledge', 'P endorsement is for?', '["Placards","Passengers","Petroleum","Permits"]'::jsonb, 1, 'P endorsement for passenger vehicles.'),
('general_knowledge', 'S endorsement is for?', '["Speed","School bus","Straight truck","Special cargo"]'::jsonb, 1, 'S endorsement for school buses.'),
('general_knowledge', 'Doubles/Triples endorsement is?', '["D","K","T","X"]'::jsonb, 2, 'T endorsement for doubles/triples.'),
('general_knowledge', 'X endorsement combines what endorsements?', '["H and N","H and T","N and P","P and S"]'::jsonb, 1, 'X combines Hazmat (H) and Tank (T) endorsements.'),
('general_knowledge', 'Background check required for which endorsement?', '["N","P","H","T"]'::jsonb, 2, 'H (Hazmat) endorsement requires TSA background check.'),
('general_knowledge', 'CDL knowledge tests are required for?', '["General knowledge only","General + endorsements","No tests required","Skills only"]'::jsonb, 1, 'Knowledge tests for general and each endorsement needed.'),
('general_knowledge', 'CDL skills test includes?', '["Pre-trip, basic control, road test","Only road test","Only written test","Only pre-trip"]'::jsonb, 0, 'Skills test includes pre-trip, basic control, and road test.'),
('general_knowledge', 'You must carry your CDL while driving a CMV.', '["True","False"]'::jsonb, 0, 'True. Must have valid CDL while driving CMV.'),
('general_knowledge', 'A CLP (Commercial Learner''s Permit) is valid for?', '["30 days","6 months","1 year","No expiration"]'::jsonb, 1, 'CLP is valid for up to 180 days and is renewable.'),
('general_knowledge', 'With CLP, you must have a CDL holder in passenger seat.', '["True","False"]'::jsonb, 0, 'True. Licensed CDL holder must be in passenger seat.'),
('general_knowledge', 'Interstate commerce requires you to be at least?', '["18 years old","21 years old","25 years old","30 years old"]'::jsonb, 1, '21 years old minimum for interstate commercial driving.'),
('general_knowledge', 'Intrastate commerce minimum age varies but often?', '["16","18","25","30"]'::jsonb, 1, 'Most states allow intrastate CDL at 18 if not crossing state lines.'),
('general_knowledge', 'Medical card must be carried?', '["Always while driving CMV","Only during interstate","Never","Only at home terminal"]'::jsonb, 0, 'Must carry valid medical card while driving CMV.'),
('general_knowledge', 'Failure to report medical condition can result in?', '["Nothing","CDL suspension or revocation","Warning only","Fine only"]'::jsonb, 1, 'Unreported medical conditions can lead to CDL suspension/revocation.'),
('general_knowledge', 'What is the "120-hour / 7-day restart" rule?', '["Must sleep 120 hours","34 hours off resets 60/70 hours","Can drive 120 hours straight","None"]'::jsonb, 1, '34 consecutive hours off duty resets your 60/70 hour clock.'),
('general_knowledge', 'Sleeper berth provision allows splitting of off-duty time?', '["True","False"]'::jsonb, 0, 'True. Sleeper berth time can be split under certain rules.'),
('general_knowledge', 'Perception time is typically how long?', '["1/4 second","3/4 second","1.5 seconds","3 seconds"]'::jsonb, 1, 'Average perception time is about 3/4 second.'),
('general_knowledge', 'Reaction time is typically how long?', '["1/4 second","3/4 second","1.5 seconds","3 seconds"]'::jsonb, 1, 'Average reaction time is about 3/4 second.'),
('general_knowledge', 'At 55 mph, perception + reaction distance is about?', '["50 feet","100 feet","120 feet","200 feet"]'::jsonb, 2, 'At 55 mph, about 120 feet to perceive and react.'),
('general_knowledge', 'Brake lag for air brakes is?', '["Zero","About 1/2 second","About 2 seconds","About 5 seconds"]'::jsonb, 1, 'Air brake lag is about 1/2 second for brakes to engage.'),
('general_knowledge', 'Engine retarders work best at?', '["Low RPM","High RPM","Idle","Any RPM equally"]'::jsonb, 1, 'Engine retarders work best at high RPM.'),
('general_knowledge', 'Most rollovers occur on?', '["Straight roads","Curves and ramps","At stops","Highways only"]'::jsonb, 1, 'Most rollovers occur on curves and exit/entrance ramps.'),
('general_knowledge', 'Off-tracking is worst for?', '["Short wheelbase vehicles","Long wheelbase vehicles","All vehicles equally","Motorcycles"]'::jsonb, 1, 'Longer wheelbases have more off-tracking in turns.'),
('general_knowledge', 'To avoid hitting overhead obstacles, know your vehicle''s?', '["Weight","Height","Length","Width"]'::jsonb, 1, 'Know vehicle height to avoid bridges, overpasses, and structures.'),
('general_knowledge', 'Fully loaded trucks and tractors require how many tie-downs?', '["1 per 5 feet","1 per 10 feet of cargo","2 minimum plus 1 per 10 feet","None required"]'::jsonb, 2, 'Need 2 minimum, plus 1 for each 10 feet of cargo length.'),
('general_knowledge', 'Baffled tanks reduce?', '["Weight","Liquid surge","Speed","Fuel consumption"]'::jsonb, 1, 'Baffles reduce liquid surge in tankers.'),
('general_knowledge', 'Smooth bore tanks are used for?', '["Sanitary products like milk","Gasoline","Water","All liquids"]'::jsonb, 0, 'Smooth bore (no baffles) for sanitary loads like milk.'),
('general_knowledge', 'What is "outage" in tankers?', '["Fuel exhaustion","Space for liquid expansion","Leak detection","Rest stop"]'::jsonb, 1, 'Outage is empty space for liquid expansion in tanks.'),
('general_knowledge', 'How much can liquids expand?', '["1%","5%","10%","Varies by liquid"]'::jsonb, 3, 'Expansion varies by liquid type and temperature change.'),
('general_knowledge', 'The most common cause of truck fires is?', '["Engine problems","Electrical problems","Driver errors","Cigarettes"]'::jsonb, 1, 'Electrical shorts and problems are most common fire cause.'),
('general_knowledge', 'During mountain driving, which gear should you be in?', '["Highest possible","Lowest possible","Same as going up","Neutral"]'::jsonb, 2, 'Use same gear going down that you used going up.'),
('general_knowledge', 'Mirrors should be checked every?', '["1-2 seconds","5-8 seconds","15-20 seconds","Once per mile"]'::jsonb, 1, 'Check mirrors every 5-8 seconds.'),
('general_knowledge', 'Front tire blowout is more dangerous than rear because?', '["Tires are bigger","Affects steering","Makes more noise","No difference"]'::jsonb, 1, 'Front blowout affects steering control.'),
('general_knowledge', 'If you get sleepy while driving, the best remedy is?', '["Coffee","Cold air","Loud music","Sleep"]'::jsonb, 3, 'Only sleep will truly restore alertness.'),
('general_knowledge', 'The "Smith System" is a method of?', '["Shifting","Braking","Defensive driving","Pre-trip inspection"]'::jsonb, 2, 'Smith System is a defensive driving method.'),
('general_knowledge', 'Triangular warning signs indicate?', '["Railroad","Stop ahead","Caution/warning","Information"]'::jsonb, 2, 'Triangular signs indicate caution or warning.'),
('general_knowledge', 'Diamond-shaped signs indicate?', '["Warning","Regulatory","Guide","School zone"]'::jsonb, 0, 'Diamond-shaped signs are warning signs.'),
('general_knowledge', 'Octagonal signs mean?', '["Yield","Stop","Warning","Information"]'::jsonb, 1, 'Octagon shape means stop.'),
('general_knowledge', 'Pennant-shaped signs mean?', '["No passing zone","School zone","Railroad","Construction"]'::jsonb, 0, 'Pennant shape indicates no passing zone.'),
('general_knowledge', 'Green signs indicate?', '["Warning","Guide/direction","Regulatory","Construction"]'::jsonb, 1, 'Green signs provide guide and directional information.'),
('general_knowledge', 'Orange signs indicate?', '["Railroad","Construction zone","School","Hospital"]'::jsonb, 1, 'Orange signs indicate construction/work zones.'),
('general_knowledge', 'Yellow signs indicate?', '["No passing","Warning/caution","Stop ahead","Interstate route"]'::jsonb, 1, 'Yellow signs warn of conditions ahead.'),
('general_knowledge', 'White signs are typically?', '["Warning","Regulatory","Guide","Construction"]'::jsonb, 1, 'White signs are regulatory (speed limits, etc.).'),
('general_knowledge', 'Blue signs indicate?', '["Warning","Motorist services","Regulatory","Construction"]'::jsonb, 1, 'Blue signs indicate motorist services (food, gas, etc.).'),
('general_knowledge', 'Brown signs indicate?', '["School zone","Recreation/cultural","Warning","Regulatory"]'::jsonb, 1, 'Brown signs indicate recreation and cultural sites.'),
('general_knowledge', 'Crossbuck sign means?', '["Stop ahead","Railroad crossing","School zone","No trucks"]'::jsonb, 1, 'White X-shaped crossbuck indicates railroad crossing.'),
('general_knowledge', 'Following too closely is also called?', '["Speeding","Tailgating","Drafting","Pacing"]'::jsonb, 1, 'Tailgating is following too closely.'),
('general_knowledge', 'Space cushion refers to?', '["Vehicle suspension","Safe space around vehicle","Tire pressure","Cargo padding"]'::jsonb, 1, 'Space cushion is safe space on all sides of vehicle.'),
('general_knowledge', 'When being passed, you should?', '["Speed up","Move right and slow down if needed","Brake suddenly","Block the passer"]'::jsonb, 1, 'Stay right and slow slightly if needed when being passed.'),
('general_knowledge', 'Right-of-way should?', '["Always be taken","Be yielded when necessary","Never be given","Only at signals"]'::jsonb, 1, 'Yield right-of-way to prevent accidents, even if you have it.'),
('general_knowledge', 'When visibility is less than 500 feet, you should?', '["Use high beams","Use parking lights","Use low beams","Turn off lights"]'::jsonb, 2, 'Use low beams in reduced visibility.'),
('general_knowledge', 'Crosswinds are most dangerous for?', '["Low, heavy vehicles","Empty, high-profile vehicles","All vehicles equally","Motorcycles only"]'::jsonb, 1, 'Empty, high-profile vehicles are most affected by crosswinds.'),
('general_knowledge', 'Merging lanes require you to?', '["Stop and wait","Match speed of traffic and merge safely","Have right-of-way","Sound horn"]'::jsonb, 1, 'Match traffic speed and merge when safe.'),
('general_knowledge', 'Blind spots for trucks are called?', '["Danger zones","No zones","Kill zones","Dark areas"]'::jsonb, 1, 'Truck blind spots are called "No Zones."'),
('general_knowledge', 'The danger of a rollover increases when?', '["Speed is low","Center of gravity is high","Cargo is light","Going straight"]'::jsonb, 1, 'High center of gravity increases rollover risk.'),
('general_knowledge', 'Jackknifing occurs when?', '["Engine overheats","Drive wheels lose traction and trailer swings","Tires blow out","Steering fails"]'::jsonb, 1, 'Jackknife happens when drive wheels skid and trailer swings around.'),
('general_knowledge', 'To prevent jackknifing?', '["Speed up","Brake hard","Keep drive wheels from skidding","Turn sharply"]'::jsonb, 2, 'Prevent jackknife by not letting drive wheels skid.'),
('general_knowledge', 'Trailer swing differs from jackknife because?', '["Trailer wheels lock, not drive wheels","Same thing","Only happens with doubles","Involves steering"]'::jsonb, 0, 'Trailer swing: trailer wheels lock. Jackknife: drive wheels skid.'),
('general_knowledge', 'Work zone speed limits should be?', '["Ignored if no workers visible","Followed at all times when posted","Exceeded if running late","Only for trucks"]'::jsonb, 1, 'Always follow posted work zone speed limits.'),
('general_knowledge', 'Fines in work zones are typically?', '["Reduced","Same as normal","Doubled","Eliminated"]'::jsonb, 2, 'Work zone fines are typically doubled.'),
('general_knowledge', 'If engine catches fire while driving?', '["Keep driving to fan flames out","Stop immediately, turn off engine, fight fire","Speed up","Jump out immediately"]'::jsonb, 1, 'Stop safely, turn off engine, fight fire if safe.'),
('general_knowledge', 'Safe following distance in heavy traffic is?', '["1 second","2 seconds","4 seconds minimum","As close as possible"]'::jsonb, 2, 'Minimum 4 seconds following distance even in heavy traffic.'),
('general_knowledge', 'Passing on two-lane roads requires?', '["Clear sight distance","Safe speed differential","No oncoming traffic","All of these"]'::jsonb, 3, 'All factors required for safe passing.'),
('general_knowledge', 'The safest attitude for a professional driver is?', '["Aggressive - get there fast","Defensive - expect others to make mistakes","Passive - let others lead","Competitive"]'::jsonb, 1, 'Defensive driving: expect others to make mistakes.'),
('combinations', 'What two things are important to prevent rollover?', '["Keep the cargo as close to the ground as possible and drive slowly around turns","Keep the cargo high and drive fast","Keep the cargo heavily distributed to the rear","Use the trailer hand brake in turns"]'::jsonb, 0, 'To prevent rollovers, keep the center of gravity low and drive slowly around corners.'),
('combinations', 'When you turn suddenly while pulling doubles, which trailer is most likely to turn over?', '["The front trailer","The rear trailer","The tractor","They are all equally likely"]'::jsonb, 1, 'The rear trailer is most susceptible to the "crack-the-whip" effect and rollover.'),
('combinations', 'Why should you not use the trailer hand valve while driving?', '["It should never be marked","It changes the air pressure too much","It prevents the brake lights from working","It can cause the trailer wheels to lock up and cause a skid"]'::jsonb, 3, 'Using the hand valve while driving can cause trailer lockup and jackknife.'),
('combinations', 'What is off-tracking?', '["When the vehicle goes off the road","When the rear wheels follow a different path than the front wheels","When the engine loses power","When the trailer disconnects"]'::jsonb, 1, 'Off-tracking (or cheating) causes the rear wheels to follow a tighter path than the front.'),
('combinations', 'What is the "trailer air supply" control?', '["A red eight-sided knob","A yellow six-sided knob","A round blue knob","A square black knob"]'::jsonb, 0, 'The trailer air supply control is a red, eight-sided knob used to supply the trailer air system.'),
('combinations', 'The trailer air supply control (red knob) should automatically pop out when air pressure falls to what range?', '["60 to 80 psi","20 to 45 psi","100 to 120 psi","0 to 10 psi"]'::jsonb, 1, 'The valve should automatically close (pop out) between 20 and 45 psi.'),
('combinations', 'When connecting the glad hands, you should press the two seals together with the couplers at what angle to each other?', '["180 degrees","45 degrees","90 degrees","Parallel"]'::jsonb, 2, 'Press the seals together at a 90-degree angle and then turn to lock.'),
('combinations', 'Why should you be sure that the fifth wheel plate is greased as required?', '["To prevent steering problems","To keep the trailer from sliding","To reduce noise","To increase fuel efficiency"]'::jsonb, 0, 'A dry fifth wheel can cause steering problems due to friction.'),
('combinations', 'After coupling, how much space should be between the upper and lower fifth wheel?', '["About 1 inch","None","Answer depends on the load","About 2 inches"]'::jsonb, 1, 'There should be no space between the upper and lower fifth wheel plates.'),
('combinations', 'Which part of the kingpin should the locking jaws close around?', '["The head","The shank","The base","The tip"]'::jsonb, 1, 'The locking jaws must close around the shank (the narrow part) of the kingpin.'),
('combinations', 'The landing gear is:', '["The tires on the trailer","The supports used to hold up the front of the trailer when not coupled","The transmission gear for hills","The steps to the cab"]'::jsonb, 1, 'Landing gear supports the trailer when it is not connected to a tractor.'),
('combinations', 'Where should the tractor be when you inspect the landing gear after uncoupling?', '["Completely cleared from the trailer","With the frame under the trailer","Attached to the trailer","Miles away"]'::jsonb, 1, 'Keep the tractor frame under the trailer in case the landing gear collapses.'),
('combinations', 'A driver crosses the air lines when hooking up to an old trailer. What will happen?', '["The hand valve will work but the pedal will not","If the trailer has no spring brakes, you could drive away but would not have trailer brakes","The brake lights will not work","The truck will not start"]'::jsonb, 1, 'If lines are crossed, air won''t reach the tanks. Without spring brakes, you might move but have no service brakes.'),
('combinations', 'You supply air to the trailer tanks by:', '["Pushing in the trailer air supply valve","Pulling out the trailer air supply valve","Connecting the service line","Using the hand valve"]'::jsonb, 0, 'Pushing in the red knob supplies air to the trailer emergency lines and tanks.'),
('combinations', 'The front trailer supports are up and the trailer is resting on the tractor. Make sure:', '["There is enough clearance between the tractor frame and the landing gear","There is enough clearance between the tops of the tractor tires and the trailer floor","There is enough clearance between the tractor cab and the trailer corners during turns","All of the above"]'::jsonb, 3, 'Check for all these clearances to prevent damage during turns.'),
('combinations', 'The safety catch for the fifth wheel locking lever must be ___ for a coupling to be complete.', '["Over the locking lever","Under the locking lever","Straight up","Released"]'::jsonb, 0, 'The safety latch must be engaged over the locking lever.'),
('combinations', 'You have pushed the trailer air supply valve (red knob). You should not move the tractor until the whole air system is:', '["At normal pressure","Empty","Between 60 and 80 psi","Leaking"]'::jsonb, 0, 'Wait for the system to reach normal pressure (usually 100-125 psi) before moving.'),
('combinations', 'How do you check that the trailer brakes are working?', '["Drive 50 mph and slam the brakes","Use the trailer hand valve to apply trailer brakes, then pull gently against them in low gear","Ask a helper to listen","Look at the gauge"]'::jsonb, 1, 'Test trailer brakes by applying the hand valve and pulling gently against them.'),
('combinations', 'What color are the service air lines usually?', '["Red","Blue","Green","Yellow"]'::jsonb, 1, 'Service lines are blue; emergency lines are red.'),
('combinations', 'Why should you lock the tractor glad hands to each other (or dummy couplers) when not towing a trailer?', '["To keep them clean","To prevent air leaks","To keep water and dirt out of the lines","All of the above"]'::jsonb, 3, 'This protects the lines from contamination and leaks.'),
('combinations', 'Which of these vehicles off-tracks the most?', '["5-axle tractor towing a 45-foot trailer","5-axle tractor towing a 53-foot trailer","Straight truck","Bobtail tractor"]'::jsonb, 1, 'Longer wheelbases off-track more. The 53-foot trailer setup is the longest.'),
('combinations', 'When backing a tractor under a trailer, you should:', '["Approach the trailer at a sharp angle","Always use the lowest reverse gear","Hit the trailer hard to ensure coupling","Keep the air lines connected"]'::jsonb, 1, 'Use the lowest reverse gear to move slowly and control the coupling.'),
('combinations', 'If the service line comes apart while you are driving, but the emergency line stays together, what happens immediately?', '["The emergency trailer brakes will come on","The trailer tank will lose pressure","Nothing happens until you try to apply the brakes","The engine will stop"]'::jsonb, 2, 'You will lose service braking to the trailer, but emergency brakes won''t apply until air is lost or you brake hard.'),
('combinations', 'Where are shut-off valves?', '["At the front of the tractor","At the back of the trailer","Under the dash","In the engine"]'::jsonb, 1, 'Shut-off valves are at the rear of trailers to close the lines for the last trailer.'),
('combinations', 'To unlock the fifth wheel, pull the release handle to the ___ position.', '["Closed","Open","Up","Down"]'::jsonb, 1, 'Pull the handle to the open position to retract the locking jaws.'),
('combinations', 'When uncoupling, you should lower the landing gear until it:', '["Just touches the ground","Makes firm contact with the ground","Lifts the trailer off the fifth wheel","Is fully extended"]'::jsonb, 1, 'Lower until firm contact, then turn handle a few extra turns to take weight off tractor.'),
('combinations', 'When should you use chocks (blocks)?', '["Only when parking on a hill","When parking a trailer without spring brakes","Never","Only in winter"]'::jsonb, 1, 'Trailers without spring brakes can roll if air leaks away - always use chocks.'),
('combinations', 'For a coupling to be complete, the fifth wheel must be:', '["Tilted down in back","Tilted up in back","Level","Greased"]'::jsonb, 0, 'The fifth wheel shoud serve as a ramp, so it is usually tilted down in the rear.'),
('combinations', 'A converter dolly is used to:', '["Couple a tractor to a trailer","Couple two trailers together","Lock the fifth wheel","Load cargo"]'::jsonb, 1, 'A converter dolly allows a second trailer to be coupled to the first.'),
('combinations', 'What happens if you try to couple when the trailer is too high?', '["It will couple perfectly","The kingpin may not engage the jaws","It may miss the fifth wheel and hit the tractor cab","The landing gear will break"]'::jsonb, 2, 'If too high, the kingpin might jump the fifth wheel and damage the cab.'),
('combinations', 'What is the most common problem with coupling?', '["Trailer is too high","Trailer is too low","Tractor is not straight","Air lines are crossed"]'::jsonb, 0, 'The most common problem is attempting to couple when the trailer is too high.'),
('combinations', 'What is the purpose of the emergency air line?', '["Controls the service brakes","Supplies air to the trailer air tanks and controls the emergency brakes","Cools the brakes","Lubricates the system"]'::jsonb, 1, 'The emergency line (red) supplies air to the trailer tanks and controls the emergency brakes.'),
('combinations', 'Before backing under a trailer, you should line up:', '["Directly in front of the trailer","At a 45 degree angle","From the side","Wherever you can"]'::jsonb, 0, 'Line up directly in front of the trailer to ensure a straight coupling.'),
('combinations', 'When hooking up, you should release the fifth wheel locking latch:', '["Before backing under the trailer","After backing under the trailer","It releases automatically","Never"]'::jsonb, 2, 'Most modern fifth wheels have an automatic release, but you should check that it is open/ready before coupling.'),
('combinations', 'How much grease should be on the fifth wheel plate?', '["A thin layer","None","A heavy coat","Only on the edges"]'::jsonb, 2, 'A heavy coat of grease is required to prevent steering problems and wear.'),
('combinations', 'The parking brake control for a trailer is usually:', '["A yellow diamond-shaped knob","A red octagonal knob","A blue round knob","A lever"]'::jsonb, 0, 'The specific *trailer* supply valve is red octagonal, but the *tractor* parking brake is yellow diamond. The red knob controls trailer spring brakes.'),
('combinations', 'Which of these statements is true?', '["Bobtail tractors can take longer to stop than loaded combination vehicles","Loaded trucks take longer to stop than empty ones","The braking distance is the same for all vehicles","Empty trucks stop faster than loaded ones"]'::jsonb, 0, 'Bobtail (empty) tractors can take longer to stop because they have less traction and bounce more.'),
('combinations', 'When should you check the airline connections?', '["Once a week","Every time you hook up to a trailer","Only if you hear a leak","During the annual inspection"]'::jsonb, 1, 'Check airline connections every time you couple to ensure they are secure and not leaking.'),
('combinations', 'If the trailer has no spring brakes, and the air pressure drops:', '["The brakes will lock up automatically","The trailer could roll away if not chocked","The emergency line will close","Nothing happens"]'::jsonb, 1, 'Without spring brakes, there is no mechanical force to hold the brakes if air is lost.'),
('combinations', 'When you are coupling, the trailer should be valid at what height?', '["Where the kingpin is even with the fifth wheel","Where the trailer lifts the tractor slightly","Where the tractor lifts the trailer slightly","Where the landing gear is way off the ground"]'::jsonb, 2, 'The tractor should lift the trailer slightly when coupling to ensure the kingpin is at the right height.'),
('combinations', 'What is the danger of "bobtailing" in wet weather?', '["It is safer","It is harder to steer","It is easier to skid","It uses more fuel"]'::jsonb, 2, 'Bobtailing (driving without a trailer) reduces weight on rear axles, making skidding more likely.'),
('combinations', 'When hauling a heavy load, you should avoid:', '["Using the engine brake","Turning widely","Quick lane changes","Stopping often"]'::jsonb, 2, 'Quick lane changes with a heavy loads can cause rollovers due to the "crack-the-whip" effect.'),
('combinations', 'What is the minimum number of tiedowns for a 20-foot load?', '["1","2","3","4"]'::jsonb, 1, 'You need at least one tiedown for every 10 feet of cargo, and at least 2 tiedowns total.'),
('combinations', 'The "no-zone" refers to:', '["Areas where trucks cannot go","Blind spots around a truck","Parking prohibited areas","Speed limit zones"]'::jsonb, 1, 'No-zones are the danger areas (blind spots) around trucks where crashes are more likely.'),
('combinations', 'If you swing wide to the left before turning right, another driver may:', '["Stop behind you","Try to pass you on the right","Honk at you","Follow you"]'::jsonb, 1, 'Swinging left creates a gap that other drivers might try to squeeze into on your right.'),
('combinations', 'You are coupling a semi-trailer to your tractor but have not yet backed under. The trailer is at the right height when:', '["The kingpin is about 1/4 inch above the fifth wheel","The end of the kingpin is even with the top of the fifth wheel","It will be raised slightly when the tractor is backed under it","The kingpin is lower than the fifth wheel"]'::jsonb, 2, 'The trailer should be slightly lower than the fifth wheel so it lifts up onto it.'),
('combinations', 'When checking the trailer''s emergency brakes, the tractor protection control valve should be placed in the ___ position.', '["Normal","Emergency","Neutral","Exhaust"]'::jsonb, 1, 'Placing the valve in emergency (or pulling the red knob) evacuates air to test the spring brakes.'),
('combinations', 'You have a major leak in the service line and you put on the brakes. Service air pressure will escape and cause the:', '["Trailer emergency brakes to come on","Tractor spring brakes to lock","Air compressor to stop","Engine to stall"]'::jsonb, 0, 'Loss of service line pressure will trigger the emergency brakes on the trailer.'),
('combinations', 'To stop a trailer skid, you should:', '["Use the trailer hand brake","Release the service brakes","Steer hard left","Accelerate"]'::jsonb, 1, 'Release the brakes to allow the wheels to regain traction.'),
('combinations', 'The tractor protection valve will close and the trailer emergency brakes will come on when there is a major leak in the ___ brake line.', '["Select","Emergency","Service","Parking"]'::jsonb, 1, 'A major leak in the emergency line causes pressure loss that triggers the emergency brakes.'),
('combinations', 'When should you lower the landing gear?', '["Before uncoupling","After uncoupling","While driving","Never"]'::jsonb, 0, 'Lower the landing gear before uncoupling to support the trailer.'),
('combinations', 'If you cannot make a turn without entering another traffic lane, you should:', '["Turn wide as you complete the turn","Turn wide as you start the turn","Not make the turn","Honk your horn"]'::jsonb, 0, 'Turn wide as you complete the turn to keep the rear of the trailer from hitting the curb.'),
('combinations', 'When driving a set of doubles, it is necessary to close the ___ shut-off valve in the last trailer.', '["Front","Middle","Back","Side"]'::jsonb, 2, 'The back shut-off valve on the last trailer must be closed to seal the air system.'),
('combinations', 'Which of these is NOT a good thing to do when driving on slippery roads?', '["Use the retarder or jake brake","Drive slower","Increase following distance","Use gentle braking"]'::jsonb, 0, 'Retarders can cause drive wheels to lock up on slippery surfaces.'),
('combinations', 'Air lines on a combination vehicle are often colored to prevent mixing them up. The emergency line is ___ and the service line is ___.', '["Red; Blue","Blue; Red","Black; Yellow","Green; White"]'::jsonb, 0, 'Red is for Emergency, Blue is for Service.'),
('combinations', 'You are coupling a tractor and semi-trailer and have connected the air lines. Before backing under the trailer you should:', '["Pull forward","Supply air to the trailer system, then pull out the air supply knob to lock the trailer brakes","Disconnect the air lines","Check the oil"]'::jsonb, 1, 'Charge the trailer air system and set the trailer brakes to ensure it doesn''t move while coupling.'),
('combinations', 'Ideally, the fifth wheel should be inspected:', '["Monthly","Weekly","Before each trip","Annually"]'::jsonb, 2, 'Coupling devices should be inspected as part of the pre-trip inspection.'),
('combinations', 'The best way to prevent a rollover is to:', '["Maintain a high center of gravity","Drive fast in curves","Keep cargo close to the ground","Load cargo on one side"]'::jsonb, 2, 'Keeping cargo low and centered is key to preventing rollovers.'),
('combinations', 'Large combination vehicles take longer to stop when they are:', '["Empty","Fully loaded","Partially loaded","New"]'::jsonb, 0, 'Empty combination vehicles often take longer to stop than loaded ones due to less traction.'),
('combinations', 'When backing up with a trailer, you try to position your vehicle so you can back in a straight line. If you must back on a curved path, back to the:', '["s side","s side","Right side","Blind side"]'::jsonb, 0, 'Back to the driver''s side so you can see better.'),
('combinations', 'When uncoupling, disconnect the electrical cable and:', '["Leave it hanging","Plug it into the dummy receptacle","Throw it in the cab","Wrap it around the air lines"]'::jsonb, 1, 'Plug the electrical cable into the dummy receptacle (or secure it) to prevent damage.'),
('combinations', 'How much space should be between the tractor and trailer when inspecting the coupling?', '["None","1 foot","2 feet","Specifically between the upper and lower fifth wheel: NONE"]'::jsonb, 3, 'There should be no light or space visible between the upper and lower fifth wheel plates.'),
('combinations', 'Which trailer is most likely to rollover?', '["A flatbed with a low load","A tank truck with baffles","A fully loaded 53-foot van","A double trailer"]'::jsonb, 2, 'High center of gravity loads (like vans) are more prone to rollover than low flatbeds.'),
('combinations', 'When driving a combination vehicle, you should check your mirrors:', '["Every 30 seconds","Only when turning","Frequently (every 5-8 seconds)","Rarely"]'::jsonb, 2, 'Frequent mirror checks are essential for safe operation of large vehicles.'),
('air_brakes', 'Which of the following is okay to find in the air brake system?', '["Oil","Water","Air","All of the above"]'::jsonb, 2, 'The air brake system uses compressed air to make the brakes work. Water and oil are contaminants.'),
('air_brakes', 'Air loss in a single vehicle (not a combination unit) should not be more than ___ with the engine off and the brakes on.', '["1 psi per minute","2 psi per minute","3 psi per minute","4 psi per minute"]'::jsonb, 2, 'For a straight truck, air loss with brakes applied should not exceed 3 psi in one minute.'),
('air_brakes', 'A straight truck or bus air brake system cannot leak more than how many psi per minute with the engine off and the brakes released?', '["1 psi","2 psi","3 psi","4 psi"]'::jsonb, 1, 'With brakes released, a single vehicle shouldn''t leak more than 2 psi per minute.'),
('air_brakes', 'If the air system should develop a leak, what will keep air in the air tanks?', '["The tractor protection valve","The emergency relay valve","The one-way check valve","The air compressor"]'::jsonb, 2, 'One-way check valves prevent air from escaping the tanks back into the supply line.'),
('air_brakes', 'When using the parking brakes or emergency brakes, what type of pressure is being used?', '["Hydraulic pressure","Air pressure","Spring pressure","Atmospheric pressure"]'::jsonb, 2, 'Spring brakes are held back by air pressure but applied by mechanical spring pressure.'),
('air_brakes', 'What turns on the electrical stop light switch in an air brake system?', '["The brake pedal","Spring pressure","Air pressure","Electricity"]'::jsonb, 2, 'Air pressure triggers the electrical stop light switch when you push the brake pedal.'),
('air_brakes', 'What will determine how effective the spring emergency brakes or the parking brakes work?', '["The adjustment of the service brakes","The amount of air pressure","The weight of the vehicle","The steepness of the grade"]'::jsonb, 0, 'Spring brakes depend on the adjustment of the service brakes to work effectively.'),
('air_brakes', 'Which of the following statements about brakes is true?', '["The heavier a vehicle or the faster it is moving, the more heat the brakes have to absorb to stop it.","Brakes have more stopping power when they get very hot.","Air brakes take effect instantly.","All of the above."]'::jsonb, 0, 'Heat absorption increases with weight and speed, which can lead to brake fade.'),
('air_brakes', 'How do you check the free-play in manual slack adjusters?', '["Park on level ground, chock the wheels, release the parking brake, and pull hard on each slack adjuster","apply the brakes and watch the slack adjusters move","Turn the adjusting nut until tight","Measure with a ruler while someone presses the pedal"]'::jsonb, 0, 'You must release the parking brake to check free play. If it moves more than 1 inch, it needs adjustment.'),
('air_brakes', 'Which is most correct about brake use on a long and steep downgrade?', '["Use the trailer hand brake to save the tractor brakes.","Use the engine braking effect, then apply brakes until speed is reduced to about 5 mph below safe speed.","Pump the brakes constantly.","Use only the parking brake."]'::jsonb, 1, 'Use proper gear (snub braking): slow to 5 mph below safe speed, release, repeat.'),
('air_brakes', 'When do you use the parking brake in air brake equipped vehicles?', '["Only when parking on a hill","Every time you park the vehicle","Only in emergencies","Never in cold weather"]'::jsonb, 1, 'Any time you park, use the parking brake (unless very hot or wet/freezing).'),
('air_brakes', 'Why should you NOT fan the brakes on and off during long downgrades?', '["It prevents the air compressor from working","Does not allow brakes to cool","It increases air pressure too much","It wears out the pedal"]'::jsonb, 1, 'Fanning gives the brakes no time to cool down, increasing the risk of fade.'),
('air_brakes', 'If you must make a quick emergency stop, you should:', '["Pump the brakes","Steer hard to the right","Stay in a straight line and maintain control","Use the hand brake"]'::jsonb, 2, 'Brake in a way that keeps the vehicle in a straight line (CSB or Stab braking).'),
('air_brakes', 'A combination vehicle air brake system cannot leak more than ___ psi per minute with the engine off and the brakes released.', '["1 psi","2 psi","3 psi","4 psi"]'::jsonb, 2, 'For combination vehicles, allowable leakage is 3 psi/minute (released) and 4 psi/minute (applied).'),
('air_brakes', 'At 55 mph on dry pavement, the air brakes lag distance adds about __ feet to total stopping distance.', '["10 feet","32 feet","55 feet","100 feet"]'::jsonb, 1, 'Brake lag adds about 32 feet at 55 mph.'),
('air_brakes', 'Air brakes cool very slowly.', '["True","False","Only in summer","Only on hills"]'::jsonb, 0, 'True. This is why managing speed and using engine braking is critical.'),
('air_brakes', 'Excessive brake use from speed can cause too much heat to build up in the air brakes.', '["True","False","True but only on drum brakes","False, air cools them"]'::jsonb, 0, 'True. Excessive heat leads to brake fade or failure.'),
('air_brakes', 'If the low pressure alarm comes on you should:', '["Pump the brakes","Drive to the next service station","Stop and park your vehicle as soon as possible","Ignore it if brakes still work"]'::jsonb, 2, 'Stop immediately. You may lose braking ability or spring brakes may pop out.'),
('air_brakes', 'It is suggested that you drain your air tanks at the end of each week to remove dirt and oil.', '["True","False (drain daily)","False (drain monthly)","True (if dry tanks)"]'::jsonb, 1, 'Drain air tanks DAILY to remove water and oil.'),
('air_brakes', 'The s-cam drum is the most common type of foundation brake.', '["True","False","Only on trailers","Only on older trucks"]'::jsonb, 0, 'S-cam brakes are indeed the most common type.'),
('air_brakes', 'The supply pressure gauge tells you how long before the s-cam turns.', '["True","False (tells you pressure in tanks)","False (tells you brake temp)","True"]'::jsonb, 1, 'Supply pressure gauge shows air pressure in the tanks.'),
('air_brakes', 'Drum brakes are the only type of brake in which brake fade occurs.', '["True","False","True for air brakes","False for hydraulic"]'::jsonb, 1, 'Disc brakes can also fade, though drums are more susceptible.'),
('air_brakes', 'On a heavy vehicle, the emergency brake is usually held in place by spring pressure because air pressure can leak away.', '["True","False","Only on trailers","Only on hills"]'::jsonb, 0, 'Spring brakes activate when air pressure is lost, acting as a fail-safe.'),
('air_brakes', 'Some vehicles have a separate air tank which can be used to release the spring brakes.', '["True (dual parking control valves)","False","Only on buses","Only on tankers"]'::jsonb, 0, 'Yes, this allows you to move the vehicle a short distance in an emergency.'),
('air_brakes', 'The two systems of a dual air brake system are the primary and secondary.', '["True","False","Service and Emergency","Front and Rear"]'::jsonb, 0, 'Dual air systems have Primary (usually rear) and Secondary (usually front) systems.'),
('air_brakes', 'When the brakes are very hot you should not use the parking brake.', '["True","False","Only if wet","Never true"]'::jsonb, 0, 'Hot drums expand; as they cool and shrink, engaged parking brakes can crack them.'),
('air_brakes', 'The total stopping distance traveling at 55 mph under normal conditions is about 100 feet.', '["True","False","Depends on weight","True for empty trucks"]'::jsonb, 1, 'False. It is over 450 feet for a heavy vehicle at 55 mph.'),
('air_brakes', 'Modern air brake systems use three braking systems - service, parking and emergency.', '["True","False","Only two","Only one"]'::jsonb, 0, 'Service (pedal), Parking (valve), and Emergency (springs/air loss) systems.'),
('air_brakes', 'The air compressor governor controls:', '["The speed of the engine","When the air compressor pumps air into the storage tanks","The air pressure in the tires","The brake pedal pressure"]'::jsonb, 1, 'The governor controls the cut-in and cut-out pressure of the compressor.'),
('air_brakes', 'The alcohol evaporator:', '["Condenses air","Cools the brakes","Reduces the risk of ice in air brake valves","Heating the cab"]'::jsonb, 2, 'Alcohol prevents moisture from freezing in the air lines and valves.'),
('air_brakes', 'The first thing to do when a low pressure warning comes on is:', '["Pump the brakes","Stop and safely park as soon as possible","Shift to neutral","Turn on hazards"]'::jsonb, 1, 'Immediate action is required to prevent emergency brake lockup.'),
('air_brakes', 'Air braking takes more time than hydraulic braking because air brakes:', '["Have stronger springs","Need to have air flow through the lines to work (Brake Lag)","Are heavier","Use different shoes"]'::jsonb, 1, 'Air takes time to flow through the lines, causing a slight delay (brake lag).'),
('air_brakes', 'The driver must be able to see a warning that is given when air pressure in the service air tanks falls below:', '["80 psi","60 psi","40 psi","20 psi"]'::jsonb, 1, 'Low air warning must activate by 60 psi (or one-half governor cut-out).'),
('air_brakes', 'An air brake system safety relief valve opens at about:', '["100 psi","120 psi","150 psi","180 psi"]'::jsonb, 2, 'The safety valve protects the tank from over-pressure, usually at 150 psi.'),
('air_brakes', 'When some air brakes in the system are doing more work than others:', '["They will last longer","They will develop more heat and vehicle handling will be affected","It is normal","The system balances itself"]'::jsonb, 1, 'Unbalanced brakes cause uneven wear, heat buildup, and potential jackknifing.'),
('air_brakes', 'Your safety relief valve has opened several times. This means:', '["The system is working well","The pressure is too low","The system needs immediate attention","You are braking too hard"]'::jsonb, 2, 'It means the governor failed to cut out, leading to dangerous over-pressure.'),
('air_brakes', 'To test the air compressor on a dual air brake vehicle, run the engine at a fast idle. Pressure should build from 85 to 100 psi within:', '["30 seconds","45 seconds","60 seconds","2 minutes"]'::jsonb, 1, 'Pressure must build quickly (45 seconds) to ensure safety.'),
('air_brakes', 'Vehicles with air brakes must have:', '["A supply pressure gauge","An air pressure gauge","A backup hydraulic system","A spare tank"]'::jsonb, 1, 'You must be able to monitor the available air pressure.'),
('air_brakes', 'Which brake system applies and releases the brakes when the driver uses the brake pedal?', '["The parking brake system","The emergency brake system","The service brake system","The auxiliary system"]'::jsonb, 2, 'The service brake system is controlled by the foot pedal.'),
('air_brakes', 'The purpose of engine retarders is to:', '["Make noise","Help slow the vehicle and reduce brake wear","Improve fuel economy","Warm up the engine"]'::jsonb, 1, 'Retarders assist in braking, saving the service brakes for stopping.'),
('air_brakes', 'The cut-in pressure for the air compressor is commonly set at:', '["85 psi","100 psi","125 psi","150 psi"]'::jsonb, 1, 'Compressor starts pumping again around 100 psi.'),
('air_brakes', 'The cut-out pressure for the air compressor is commonly set at:', '["100 psi","125 psi","150 psi","175 psi"]'::jsonb, 1, 'Compressor stops pumping around 125 psi.'),
('air_brakes', 'What can happen if the air pressure gets too low in an air brake system?', '["The engine will stop","The emergency brakes will engage automatically","The compressor will explode","Steering will lock"]'::jsonb, 1, 'If pressure drops (usually 20-45 psi), spring brakes automatically apply.'),
('air_brakes', 'Why should you never leave a parked vehicle with the air brakes released?', '["It saves air","It cools the brakes","It could roll away","It drains the battery"]'::jsonb, 2, 'Without parking brakes (or chocks), the vehicle has nothing holding it.'),
('air_brakes', 'How should you test the low pressure warning signal?', '["Pump the pedal with engine running","Pump the brake pedal with the engine off / key on","Drain the tanks manually","Looking at the gauge"]'::jsonb, 1, 'Engine off, key on (for electrical warnings), pump pedal to lower pressure.'),
('air_brakes', 'What is a "wig wag"?', '["A type of trailer","A mechanical low air pressure warning device","A loose wheel","A steering problem"]'::jsonb, 1, 'A wig wag is a mechanical arm that drops into view when air pressure is low.'),
('air_brakes', 'The brake pedal in an air brake system:', '["Controls the speed of the air compressor","Controls the air pressure applied to put on the brakes","Is connected to the engine","Works directly on the master cylinder"]'::jsonb, 1, 'Pushing harder applies more air pressure, braking harder.'),
('air_brakes', 'All air brake equipped vehicles have:', '["A supply pressure gauge","An alcohol evaporator","A hydraulic backup","Air conditioning"]'::jsonb, 0, 'A supply pressure gauge is required.'),
('air_brakes', 'The application pressure gauge shows how much air pressure you:', '["Have in the tanks","Are applying to the brakes","Have lost","Need to stop"]'::jsonb, 1, 'This gauge shows the pressure currently being applied to the brake chambers.'),
('air_brakes', 'Front wheel brakes are good under all conditions.', '["True","False","Only in dry weather","Only when loaded"]'::jsonb, 0, 'True. Front brakes are essential for stopping and do not cause skids if working right.'),
('air_brakes', 'Automatic slack adjusters should be manually adjusted:', '["Every week","Every day","Only during installation or when the mechanism fails","Never"]'::jsonb, 2, 'Manually adjusting auto-slacks can hide mechanical problems. Only do it for repairs.'),
('air_brakes', 'Brake linings (drums) should not be:', '["Thick","Loose or soaked with oil/grease","Hot","Dusty"]'::jsonb, 1, 'Oil/grease causes brake failure; loose linings are dangerous.'),
('air_brakes', 'The modulating control valve allows you to:', '["Control the spring brakes gradually","Control the compressor","Control the air horn","Drain the tanks"]'::jsonb, 0, 'This lever lets you apply spring brakes gradually to stop if service brakes fail.'),
('air_brakes', 'Dual parking control valves allow you to:', '["Park twice","Release the spring brakes using air from a separate tank","Use hydraulic brakes","Lock the steering"]'::jsonb, 1, 'This lets you move a vehicle with failed primary air by using a reserve tank.'),
('air_brakes', 'The parking brake control is a:', '["Yellow diamond-shaped knob","Red octagon","Blue circle","Green square"]'::jsonb, 0, 'Push-pull control knob for parking brakes is yellow and diamond-shaped.'),
('air_brakes', 'ABS is an addition to your normal brakes. It does not:', '["Shorten stopping distance","Increase or decrease normal braking capability","Prevent skids","Work on wet roads"]'::jsonb, 1, 'ABS only activates to prevent lockup; it doesn''t change normal braking power.'),
('air_brakes', 'If your truck has a dual air system and one system is very low on pressure:', '["Drive slowly","Either the front or rear brakes will not be fully operational","The other system will compensate","You can still drive safely"]'::jsonb, 1, 'You lose half your braking ability. Do not drive.'),
('air_brakes', 'To check the air compressor governor cut-in and cut-out pressures:', '["Run the engine at a fast idle and watch the gauges","Step on and off the brake pedal","Drain the tanks","Listed to the engine"]'::jsonb, 0, 'Watch the gauge to see when pressure stops rising (cut-out) and starts rising again (cut-in).'),
('air_brakes', 'If your vehicle has an alcohol evaporator, you should check the alcohol level:', '["Daily in cold weather","Weekly","Monthly","Never"]'::jsonb, 0, 'Check and refill daily in cold weather to prevent freezing.'),
('air_brakes', 'A slack adjuster''s free play needs to be adjusted if it is more than about:', '["1/4 inch","1/2 inch","1 inch","2 inches"]'::jsonb, 2, 'More than 1 inch of free play (where the push rod enters the chamber) is unsafe.'),
('air_brakes', 'Which kind of brake requires more stopping distance?', '["Air brakes","Hydraulic brakes","Disc brakes","They are all the same"]'::jsonb, 0, 'Air brakes have added brake lag time.'),
('air_brakes', 'The brake system that applies and releases the brakes when the driver uses the brake pedal is the:', '["Emergency brake system","Service brake system","Parking brake system","Auxiliary brake system"]'::jsonb, 1, 'Service brakes are for normal driving.'),
('air_brakes', 'Cracks in the brake drum should not be longer than:', '["1/4 the width of the friction area","1/2 the width of the friction area","1 inch","Any crack is okay"]'::jsonb, 1, 'Cracks exceeding 1/2 the width of the friction area are dangerous.'),
('air_brakes', 'In air brake vehicles, the parking brake should be used:', '["Except when the brakes are very hot or in freezing wet conditions","Only on hills","Only when the vehicle is empty","Always, no matter what"]'::jsonb, 0, 'Avoid using parking brakes if they can freeze to the drums or damage hot drums.'),
('air_brakes', 'If you do not have automatic tank drains, how often should you drain the oil and water from the bottom of compressed air storage air tanks?', '["At the end of each day of driving","Once a week","Once a month","Every 2 days"]'::jsonb, 0, 'Daily draining is required to prevent system failure/freezing.'),
('air_brakes', 'The proper method of braking when going down long and/or steep grades after selecting the proper gear is to brake until your speed is about ___ mph below your "safe" speed, then release your brakes.', '["5 mph","10 mph","15 mph","20 mph"]'::jsonb, 0, 'Snub braking: slow 5 mph below safe speed, release, let speed build back, repeat.'),
('air_brakes', 'If your vehicle has an air brake system, you should check for:', '["Air leaks","Correct air pressure","Properly working gauges","All of the above"]'::jsonb, 3, 'All parts of the air system must be checked.'),
('air_brakes', 'The air loss rate for a straight truck or bus with the engine off and the brakes on should not be more than:', '["1 psi in one minute","2 psi in one minute","3 psi in one minute","4 psi in one minute"]'::jsonb, 2, '3 psi/min for single vehicles with brakes applied.'),
('air_brakes', 'The air loss rate for a combination vehicle with the engine off and the brakes on should not be more than:', '["2 psi in one minute","3 psi in one minute","4 psi in one minute","5 psi in one minute"]'::jsonb, 2, '4 psi/min for combination vehicles with brakes applied.'),
('air_brakes', 'The safety valve reduces pressure at ___ psi.', '["50","100","150","200"]'::jsonb, 2, 'It opens at 150 psi to prevent tank rupture.'),
('hazmat', 'A vehicle placarded for hazardous materials must have placards on ___ sides.', '["2","3","4","5"]'::jsonb, 2, 'Placards must be on all 4 sides: front, rear, and both sides.'),
('hazmat', 'Who is responsible for checking that the shipper correctly named, labeled, and marked a hazardous materials shipment?', '["The Manufacturer","The Carrier","The Driver","The Shipper"]'::jsonb, 1, 'To refuse improper shipments, the Carrier is responsible for checking the paperwork.'),
('hazmat', 'If there is an "RQ" before or after the item description on the shipping paper, it means the:', '["Carrier must report any spill of this material","Material is in a Reportable Quantity","Material is Really Quick","Material is Radioactive Quality"]'::jsonb, 1, 'RQ stands for Reportable Quantity.'),
('hazmat', 'You need to warn other motorists of a stopped vehicle containing hazardous materials. You may NOT use:', '["Reflective triangles","Signal fires","Flares (fusees)","Flashing lights"]'::jsonb, 2, 'Never use flares/fusees (fire) around hazardous materials.'),
('hazmat', 'A driver who transports hazardous materials in a vehicle that must be placarded must have:', '["s license","A CDL with a hazardous materials endorsement","A letter of permission","A police escort"]'::jsonb, 1, 'You need a specific Hazmat (H) endorsement on your CDL.'),
('hazmat', 'The intent of the Hazardous Materials regulations has three goals: 1. Contain the material, 2. Communicate the risk, 3. ______.', '["Assure safe drivers and equipment","Allow state enforcement","Tax the shipper","Protect the environment"]'::jsonb, 0, 'The three goals are Containment, Communication, and Assuring safe drivers/equipment.'),
('hazmat', 'To stop for railroad tracks, you should stop ___ to ___ feet before the nearest track.', '["5; 20","10; 35","15; 50","20; 100"]'::jsonb, 2, 'Stop within 15 to 50 feet of the nearest rail.'),
('hazmat', 'If you are in an accident involving hazardous materials, you should:', '["Drive away quickly","Keep people away and warn others","Open the doors","Move the cargo"]'::jsonb, 1, 'Keep people away, warn others, and prevent fire.'),
('hazmat', 'The shipping paper for hazardous materials must always be easily accessible to:', '["Emergency response personnel","The shipper","The public","The warehouse manager"]'::jsonb, 0, 'Papers must be within reach or in the door pouch for emergency responders.'),
('hazmat', 'When transporting chlorine in cargo tanks, you must have:', '["An approved gas mask in the vehicle","A fire extinguisher","Extra water","A shovel"]'::jsonb, 0, 'Chlorine transport requires an approved gas mask.'),
('hazmat', 'The total transport index of all radioactive material packages in a single vehicle must not exceed:', '["10","50","100","200"]'::jsonb, 1, 'The total transport index limit is 50.'),
('hazmat', 'If you have a hazardous materials fire on the road, you should:', '["Open the trailer doors","Drive to a fire station","Wait for firefighters","Attempt to extinguish it only if you can do so safely and without risk"]'::jsonb, 3, 'Do not open doors. Only fight the fire if safe; otherwise wait for help.'),
('hazmat', 'Class 1 hazard is:', '["Explosives","Gases","Flammable liquids","Poison"]'::jsonb, 0, 'Class 1 is Explosives.'),
('hazmat', 'Class 3 hazard is:', '["Gases","Flammable liquids","Solids","Oxidizers"]'::jsonb, 1, 'Class 3 is Flammable Liquids.'),
('hazmat', 'You should stop and check your tires every ___ hours or ___ miles when transporting hazardous materials.', '["1; 50","2; 100","3; 150","4; 200"]'::jsonb, 1, 'Check tires every 2 hours or 100 miles.'),
('hazmat', 'A placard on dragging or rubbing against part of the trailer is:', '["Acceptable","Prohibited","Common","Required"]'::jsonb, 1, 'Placards must be clear and visible, not damaged or rubbing.'),
('hazmat', 'Which marking system is used for bulk packaging?', '["NFPA","UN/NA identification number","HMIS","OSHA"]'::jsonb, 1, 'Bulk packaging uses the 4-digit UN/NA ID number.'),
('hazmat', 'Smoking is prohibited within ___ feet of a Class 3 (Flammable Liquid) cargo tank.', '["10","25","50","100"]'::jsonb, 1, 'No smoking within 25 feet of flammable liquid tanks.'),
('hazmat', 'Shipping papers must include:', '["Proper shipping name, hazard class, ID number, packing group","Price of the goods","s home address","Weather conditions"]'::jsonb, 0, 'The core description is Name, Class, ID, Group.'),
('hazmat', 'When parking a vehicle carrying Division 1.1, 1.2, or 1.3 explosives, you must not park within ___ feet of the traveled part of the road.', '["5","100","300","500"]'::jsonb, 0, 'Do not park within 5 feet of the traveled road; use a parking lot or safe haven.'),
('hazmat', 'Division 1.1 explosives must not be transported in a combination vehicle if the combination includes:', '["A tank","A flatbed","More than one trailer with the explosives","A refrigerated van"]'::jsonb, 2, 'You cannot transport 1.1 explosives in a truck-truck-trailer combination.'),
('hazmat', 'The Transport Index determines:', '["How fast you can drive","How close packages can be to people/animals","The cost of shipping","The weight of the package"]'::jsonb, 1, 'Transport Index controls stowage distance from people and film.'),
('hazmat', 'Who provides the emergency response information?', '["The driver","The shipper","The police","The truck manufacturer"]'::jsonb, 1, 'The shipper must provide ER info (MSDS/SDS or guidebook page).'),
('hazmat', 'If a label will not fit on a package, it may be placed on a tag securely attached to the package.', '["True","False","Only for liquids","Only for gases"]'::jsonb, 0, 'True, tags are acceptable if the package is too small.'),
('hazmat', 'Generally, you cannot park a vehicle carrying hazardous materials within ___ feet of an open fire.', '["100","200","300","500"]'::jsonb, 2, 'Keep 300 feet away from open fires.'),
('hazmat', 'Hazardous materials shipping papers can be distinguished from others by:', '["Being printed in red","Being first, or having an \"X\" in the HM column","Being in a separate envelope","Having a sticker on them"]'::jsonb, 1, 'They must be on top, distinct, or marked with an "X" (or "RQ") in the HM column.'),
('hazmat', 'If you discover a tire leak on a hazardous materials vehicle, you must:', '["Drive to the nearest repair shop","Stop immediately and repair/replace it","Add air and continue","Ignore it if it is a dual tire"]'::jsonb, 1, 'You cannot continue travel with a leaking tire.'),
('hazmat', 'A safe haven is:', '["A truck stop","An approved place for parking unattended vehicles loaded with explosives","A rest area","A police station"]'::jsonb, 1, 'Safe havens are government-approved parking spots for explosives.'),
('hazmat', 'How often must a driver be trained in security awareness?', '["Every year","Every 2 years","Every 3 years","Only once"]'::jsonb, 2, 'Hazmat training is required at least every 3 years.'),
('hazmat', 'When fueling a placarded vehicle, someone must always be:', '["At the nozzle, controlling the fuel flow","In the cab","Checking the tires","Cleaning the windshield"]'::jsonb, 0, 'Never leave the nozzle unattended; engine must be off.'),
('hazmat', 'Animals and human food should not be loaded in the same vehicle with:', '["Poisons","Flammable liquids","Gases","Corrosives"]'::jsonb, 0, 'Poisons cannot be transported with food/feed.'),
('hazmat', 'A driver must have a CDL with an H endorsement to haul:', '["Any amount of hazardous material","Only large quantities","Any amount requiring placards","Only explosives"]'::jsonb, 2, 'The endorsement is required for any load that requires placarding.'),
('hazmat', 'Which signal may be used to warn of a stopped vehicle containing explosives?', '["Flares","Reflective triangles","Fusees","Burning signals"]'::jsonb, 1, 'Use reflective triangles. Never use fire (flares/fusees).'),
('hazmat', 'When there is a hazardous materials emergency, you should:', '["Run away","Keep people away and upwind","Bury the material","Touch the material to identify it"]'::jsonb, 1, 'Stay upwind and keep people away.'),
('hazmat', 'Identification numbers are used to:', '["Identify the driver","Identify the truck","Identify the specific chemical or material","Identify the route"]'::jsonb, 2, 'ID numbers (UN/NA) tell responders what the chemical is.'),
('hazmat', 'If you are driving a placarded vehicle, you must minimize the use of:', '["Congested areas (tunnels, narrow streets, crowds)","Highways","Rest areas","Weigh stations"]'::jsonb, 0, 'Regulations require avoiding heavily populated or congested areas when possible.'),
('hazmat', 'You may not park a vehicle carrying hazardous materials within ___ feet of a bridge, tunnel, or building.', '["100","200","300","500"]'::jsonb, 2, 'The limit is generally 300 feet for explosives/hazmat near structures.'),
('hazmat', 'Who determines if a material is hazardous?', '["The driver","The shipper","The carrier","The DOT"]'::jsonb, 1, 'The shipper classifies the material.'),
('hazmat', 'When transporting Division 1.1, 1.2, or 1.3 explosives, you must have:', '["A written route plan","A police escort","Two drivers","A radio"]'::jsonb, 0, 'Carriers must provide a written route plan for Class 1 explosives.'),
('hazmat', 'The power unit of a placarded vehicle must have a fire extinguisher with a UL rating of ___ B:C or more.', '["5","10","20","50"]'::jsonb, 1, '10 B:C is the minimum UL rating.'),
('hazmat', 'When separation is required, packages cannot be loaded:', '["Next to each other","In the same vehicle","On top of each other","Without pallets"]'::jsonb, 0, 'Separation means they cannot be right next to each other.'),
('hazmat', 'Which hazard class uses a "flammable" placard?', '["Class 1","Class 2","Class 3","Class 4"]'::jsonb, 2, 'Class 3 is Flammable Liquids.'),
('hazmat', 'A "Dangerous" placard may be used if:', '["You have 1001 lbs or more of two or more non-bulk Table 2 materials","You are in a hurry","You have any amount of poison gas","You have explosives"]'::jsonb, 0, 'Dangerous placard is for mixed loads of Table 2 goods exceeding 1001 lbs.'),
('hazmat', 'If you are required to stop at railroad crossings, you must stop:', '["15-50 feet away","5-10 feet away","50-100 feet away","Anywhere is fine"]'::jsonb, 0, '15 to 50 feet is the rule.'),
('hazmat', 'The Emergency Response Guidebook (ERG) is primarily used by:', '["Truck drivers","First responders","Shippers","Mechanics"]'::jsonb, 1, 'Though you carry it, it is designed for firefighters and police.'),
('hazmat', 'If hazardous material is spilling from your vehicle, you should:', '["Drive to a safe spot","Not move the vehicle any more than safety requires","Try to catch it in a bucket","Wash it down with water"]'::jsonb, 1, 'Move only if absolutely necessary for safety; otherwise contain it.'),
('hazmat', 'Class 8 is:', '["Corrosives","Radioactive","Poison","Explosives"]'::jsonb, 0, 'Class 8 is Corrosives.'),
('hazmat', 'Class 6 is:', '["Poison","Radioactive","Corrosive","Flammable"]'::jsonb, 0, 'Class 6 is Poison (Toxic).'),
('hazmat', 'What shape are most placards?', '["Diamond","Square","Circle","Triangle"]'::jsonb, 0, 'Placards are diamond-shaped (square-on-point).'),
('hazmat', 'You have a vehicle without racks to hold cylinders of compressed gas. You may load the cylinders only if they are:', '["Loaded upright or braced lying down flat","Bundled together","Loose","Leaning against the wall"]'::jsonb, 0, 'Cylinders must be secured upright or braced flat.'),
('hazmat', 'When handling packages of explosives, you must:', '["Never use hooks or metal tools","Throw them gently","Roll them","Drag them"]'::jsonb, 0, 'Metal tools can cause sparks/detonation.'),
('hazmat', 'Who is responsible for packaging, labeling, and marking the hazardous material?', '["The driver","The shipper","The carrier","The receiver"]'::jsonb, 1, 'The Shipper does the packaging and marking.'),
('hazmat', 'Use this only when you are transporting hazardous materials that are not compatible with water:', '["Type B:C fire extinguisher","Type A fire extinguisher","Water hose","Sand"]'::jsonb, 0, 'B:C extinguishers are for liquids/electrical; water can spread some fires.'),
('hazmat', 'Can you transfer a Class 3 flammable liquid from one tanker to another on a public highway?', '["Yes","No, unless it is an emergency","Only at night","Only if you have a permit"]'::jsonb, 1, 'Emergency transfer only.'),
('hazmat', 'You should check the cargo markings:', '["At the start of the trip","Every time you stop","At the end of the trip","t need to check"]'::jsonb, 0, 'Start the trip by verifying all markings and placards.'),
('hazmat', 'A driver ticketed for a hazmat violation may lose their endorsement for:', '["1 year","3 years","Lifetime","It depends on the offense"]'::jsonb, 3, 'Disqualification periods vary by offense severity.'),
('passenger', 'When should you check the interior of the bus?', '["Before driving","Before and after every trip","Only when cleaning","Once a week"]'::jsonb, 1, 'You must check the interior of the bus before each trip to ensure rider safety and after each trip for left articles or sleeping passengers.'),
('passenger', 'Hazardous materials that are NOT allowed on a bus include:', '["Small-arms ammunition labeled ORM-D","Emergency hospital supplies","Class 2 poison gas","Drugs"]'::jsonb, 2, 'Poison gas (Class 2) and liquid Class 6 poisons are forbidden on buses.'),
('passenger', 'How far from a railroad crossing should you stop?', '["5 to 10 feet","15 to 50 feet","50 to 100 feet","100 to 200 feet"]'::jsonb, 1, 'Stop between 15 and 50 feet before the nearest rail.'),
('passenger', 'When arriving at the destination or intermediate stops, you should announce:', '["The time","The weather","The location, reason for stopping, next departure time, and bus number","s name"]'::jsonb, 2, 'Clear announcements prevent passengers from missing their stops or getting on the wrong bus.'),
('passenger', 'Which of the following types of cargo can never be carried on a bus?', '["Small arms ammunition","Irritating materials or tear gas","Emergency hospital supplies","Oxygen"]'::jsonb, 1, 'Tear gas and irritating materials are prohibited.'),
('passenger', 'If your bus has an emergency exit door, it must:', '["Be locked while driving","Be open for fresh air","Be closed when operating the bus","Have a warning light"]'::jsonb, 2, 'Emergency exits must be closed and secured while the bus is moving.'),
('passenger', 'You must not allow riders to stand:', '["In the aisle","In front of the standee line","When the bus is moving","Near the back"]'::jsonb, 1, 'The standee line marks the area where passengers would obstruct the driver''s view.'),
('passenger', 'Buses may carry small-arms ammunition labeled ORM-D, emergency hospital supplies, and drugs.', '["True","False","Only in cargo bays","Only if permitted by police"]'::jsonb, 0, 'These are exceptions to the hazardous materials rules for buses.'),
('passenger', 'When must you stop before crossing a drawbridge?', '["Always","Only if the light is red","At least 50 feet before the draw of the bridge","Never"]'::jsonb, 2, 'Stop at least 50 feet before the draw unless there is a traffic light or attendant.'),
('passenger', 'Which of these statements about speed management is true?', '["You should always drive at the posted speed limit","You must be able to stop within the distance you can see ahead","Buses stop faster than cars","Speed does not affect safety"]'::jsonb, 1, 'Never drive faster than the distance you can see ahead to stop.'),
('passenger', 'If a rider wants to bring a car battery or a can of gasoline aboard your bus, you should:', '["Allow it if they hold it","Put it in the cargo bay","Not allow them to do so","Charge extra"]'::jsonb, 2, 'Gasoline and car batteries (acid) are hazardous and prohibited.'),
('passenger', 'When obtaining a CDL with a passenger endorsement, you must pass a knowledge test on:', '["Driving safely and transporting passengers safely","Engine repair","Route planning","Fare collection"]'::jsonb, 0, 'The test covers safety regulations and passenger management.'),
('passenger', 'It is best to wear your seat belt:', '["Only on highways","At all times","Only in bad weather","Only if police are around"]'::jsonb, 1, 'Drivers must always wear seat belts.'),
('passenger', 'Brake-door interlocks:', '["Are required on all buses","Should be used in place of the parking brake","Must not be used in place of the parking brake","Are for emergency stops only"]'::jsonb, 2, 'Interlocks only work when the door is open; never rely on them for parking.'),
('passenger', 'Most hazardous materials are allowed on buses if they are:', '["Labeled correctly","Carried by the driver","Hidden","Prohibited entirely"]'::jsonb, 3, 'Most hazmat is prohibited. Only specific exceptions are allowed.'),
('passenger', 'At the end of each shift, you should:', '["Park and leave","Inspect the bus for lost items and sleeping passengers","Lock the doors only","Refuel only"]'::jsonb, 1, 'Always check for sleeping passengers or suspicious items.'),
('passenger', 'If your bus leans in a curve, you:', '["Are driving too fast","Have a flat tire","Are on a banked road","Should speed up"]'::jsonb, 0, 'Leaning indicates excessive speed for the curve.'),
('passenger', 'When you discharge an unruly rider, you should choose a place that is:', '["Dark and quiet","As safe as possible","Far from town","On a bridge"]'::jsonb, 1, 'Safety is the priority, even for unruly passengers.'),
('passenger', 'You should not let riders leave the bus:', '["At their stop","In a safe location","Unless they have paid","At an unsafe location"]'::jsonb, 3, 'Never discharge passengers in an unsafe place.'),
('passenger', 'How many folding aisle seats are permitted in a bus not carrying farm workers?', '["0","1","2","4"]'::jsonb, 0, 'Folding aisle seats are generally prohibited.'),
('passenger', 'The total weight of a hazardous material allowed on a bus is:', '["100 lbs","500 lbs","Any amount","None"]'::jsonb, 1, 'The limit is 500 lbs total, with no more than 100 lbs of any one class.'),
('passenger', 'If you are driving a bus with passengers, you should not fuel the bus:', '["With passengers on board","In an enclosed building with passengers on board","At night","At a truck stop"]'::jsonb, 1, 'Avoid fueling with passengers on board, and never do so in a closed building.'),
('passenger', 'You must stop your bus between 15 and 50 feet before:', '["A stop sign","A railroad crossing","A bridge","A tunnel"]'::jsonb, 1, 'This is the mandatory stopping distance for RR crossings.'),
('passenger', 'To avoid a crash, you had to drive onto the right shoulder. You are now driving at 40 mph on the shoulder. How should you move back onto the pavement?', '["Steer sharply back","If possible, come to a complete stop before steering back","Accelerate","Wait for a gap"]'::jsonb, 1, 'It is safer to stop and re-enter traffic than to steer back at speed.'),
('passenger', 'The maximum weight of hazardous materials of one class allowed on a bus is:', '["100 pounds","500 pounds","50 pounds","200 pounds"]'::jsonb, 0, '100 lbs limit per class.'),
('passenger', 'If a passenger is drunk or disruptive, you may:', '["Kick them off anywhere","Use force","s guidelines","Ignore them"]'::jsonb, 2, 'Follow company policy; usually discharge in a safe, well-lit area.'),
('passenger', 'Which of the following must be closed when the bus is in motion?', '["Windows","Emergency exit","Roof hatches","s window"]'::jsonb, 1, 'Emergency exits must be secured.'),
('passenger', 'When carrying passengers, you should inspect the bus:', '["Weekly","Before each trip","Monthly","Annually"]'::jsonb, 1, 'Pre-trip inspections are mandatory.'),
('passenger', 'Before driving, you must insure that the emergency exit window:', '["Is locked permanently","Moves freely and opens completely","Is barred","Is taped shut"]'::jsonb, 1, 'It must work properly for safety.'),
('passenger', 'If you have to swerve to avoid a hazard:', '["Brake hard while swerving","Do not brake while turning","Accelerate","Close your eyes"]'::jsonb, 1, 'Braking while swerving can cause a skid/rollover.'),
('passenger', 'Secure baggage and freight in a way that avoids damage and:', '["Allows the driver to move freely","Allows riders to exit by any window or door in an emergency","Is easy to unload","Looks neat"]'::jsonb, 1, 'Aisles and exits must remain clear.'),
('passenger', 'Bus accidents often happen at:', '["Intersections","Bridges","Tunnels","Highways"]'::jsonb, 0, 'Intersections are high-risk areas.'),
('passenger', 'You may carry up to ___ lbs of any one class of allowed hazardous materials.', '["100","200","300","500"]'::jsonb, 0, '100 lbs per class limit.'),
('passenger', 'When should you talk to passengers?', '["Only when necessary/safe","All the time","Never","To tell jokes"]'::jsonb, 0, 'Distracted driving is dangerous.'),
('passenger', 'Standee lines are:', '["For decoration","To show where passengers cannot stand","Found on all buses","To separate men and women"]'::jsonb, 1, 'Passengers standing forward of this line obstruct the driver''s view.'),
('passenger', 'Convex (spot) mirrors make things look:', '["Smaller and further away","Larger and closer","Normal size","Upside down"]'::jsonb, 0, 'Convex mirrors show a wider area but distort distance.'),
('passenger', 'During your pre-trip inspection, you should check for:', '["Passenger comfort items","Cleanliness only","Safety defects","Radio stations"]'::jsonb, 2, 'Safety is the primary focus.'),
('passenger', 'Charter bus drivers should normally:', '["Allow passengers to do whatever they want","Explain rules and safety procedures at the start","Ignore the passengers","Drive faster"]'::jsonb, 1, 'Briefing passengers on safety is the driver''s job.'),
('passenger', 'The interlock releases when you:', '["Close the door","Open the door","Push the brake","Start the engine"]'::jsonb, 0, 'Closing the door releases the brake interlock.'),
('passenger', 'You must stop at a railroad crossing unless:', '["The train has passed","A police officer or flagman signals you to proceed","You are late","There are no passengers"]'::jsonb, 1, 'Only officials can waive the stop requirement.'),
('passenger', 'Carrying a guest on a bus is prohibited unless:', '["They pay","They are family","It is an emergency","They sit in the back"]'::jsonb, 2, 'Unauthorized guests are generally prohibited except in emergencies.'),
('passenger', 'Buses rarely have:', '["Seat belts for passengers","Windows","A driver","Wheels"]'::jsonb, 0, 'Many transit/coach buses lack passenger seat belts.'),
('passenger', 'Managing space around your bus is important because:', '["Buses are large and heavy","It looks good","Police watch for it","It saves fuel"]'::jsonb, 0, 'Large vehicles need more stopping and turning distance.'),
('passenger', 'Blind spots on a bus are:', '["Non-existent","Only underneath","Directly behind and to the sides","In front"]'::jsonb, 2, 'Large blind spots exist behind and beside the bus.'),
('passenger', 'When pulling out of a stop, you should:', '["Honk your horn","Just go","Wait for a large gap in traffic","Assume they will stop"]'::jsonb, 2, 'Buses accelerate slowly, so you need a large gap.'),
('passenger', 'A common cause of bus crashes is:', '["Driving too fast for conditions","Stops","Passengers","Traffic lights"]'::jsonb, 0, 'Speeding is a major factor in accidents.'),
('passenger', 'When backing a bus:', '["It is always safe","You should use a helper/spotter if possible","Use only mirrors","Drive fast"]'::jsonb, 1, 'Backing is dangerous; use a spotter.'),
('passenger', 'If you miss your exit on a highway:', '["Back up on the shoulder","Go to the next exit","Stop in the lane","U-turn"]'::jsonb, 1, 'Never back up on a highway.'),
('passenger', 'Riders should be reminded to watch their step when:', '["Boarding or alighting","Sleeping","Eating","Talking"]'::jsonb, 0, 'Slips and falls are common during boarding/alighting.'),
('doubles_triples', 'Which of these statements about managing space to the sides is true?', '["You should avoid traveling next to others whenever possible","Strong winds are not a problem for doubles and triples","You do not have to worry about the space to your sides","Always drive in the left lane"]'::jsonb, 0, 'Doubles and triples need more space and can be pushed sideways by wind. Avoid driving next to others.'),
('doubles_triples', 'A converter dolly is:', '["A coupling device of one or two axles and a fifth wheel by which a semi-trailer can be coupled to the rear of a tractor-trailer combination","A type of trailer","A tool to change tires","A device to load cargo"]'::jsonb, 0, 'A dolly converts a semi-trailer into a full trailer.'),
('doubles_triples', 'Which of these is NOT a good thing to do when driving on slippery roads?', '["Use the engine retarder","Use gentle braking","Drive slower","Keep more following distance"]'::jsonb, 0, 'Retarders can cause the drive wheels to skid on slippery surfaces, leading to a jackknife.'),
('doubles_triples', 'The "crack-the-whip" effect is most likely to tip over:', '["The rear trailer of a triple","The tractor","The first trailer","The middle trailer"]'::jsonb, 0, 'Rearward amplification causes the last trailer to sway the most.'),
('doubles_triples', 'You should check the height of the trailer before connecting a converter dolly to a second or third trailer. The trailer height is right when:', '["The trailer will be raised slightly when the converter dolly is backed under it","The kingpin allows the fifth wheel to slide under comfortably","The center of the kingpin lines up with the locking jaws","The landing gear is fully retracted"]'::jsonb, 0, 'The dolly should slightly raise the trailer to ensure a secure coupling without high-hitching.'),
('doubles_triples', 'Doubles and triples must be driven:', '["Very fast to maintain stability","Very smoothly","Only in the daytime","Without mirrors"]'::jsonb, 1, 'Smooth driving prevents rollovers and crack-the-whip.'),
('doubles_triples', 'When you uncouple the rear trailer, you should lower the landing gear until:', '["It makes firm contact with the ground","It is 6 inches off the ground","The trailer is lifted off the dolly","The dolly is free"]'::jsonb, 0, 'Firm contact supports the trailer weight.'),
('doubles_triples', 'Driving a truck with double or triple trailers requires:', '["Less skill than a single trailer","More inspection time","Less stopping distance","No special endorsement"]'::jsonb, 1, 'There are more things to check (connections, brakes, tires).'),
('doubles_triples', 'Which of these statements about quick steering movements is true?', '["Doubles and triples are more stable than singles","Counter-steering is easier with triples","Doubles and triples flip over from quick steering moves more easily than cars or single trailers","Quick steering is safe"]'::jsonb, 2, 'High center of gravity and rearward amplification make them prone to rollover.'),
('doubles_triples', 'If you want to couple a second trailer to your combination vehicle, secure it by using:', '["The trailer emergency air brakes","Chocks","The parking brake","Both chocks and brakes"]'::jsonb, 3, 'Safety first: use both to prevent movement.'),
('doubles_triples', 'You are driving with double trailers and must use your brakes to avoid a crash. For emergency braking you should:', '["Use controlled or stab braking","Use only the trailer brakes","Use only the tractor brakes","Turn the steering wheel back and forth"]'::jsonb, 0, 'Controlled or stab braking keeps the vehicle in a straight line.'),
('doubles_triples', 'When coupling a second trailer, you should check that:', '["The pintle hook is latched","The dolly air tank is drained","The safety chains are loose","The light cord is disconnected"]'::jsonb, 0, 'A latched pintle hook is critical for connection.'),
('doubles_triples', 'How much space should you leave between you and the vehicle in front of you?', '["1 second for every 10 feet of vehicle length, plus 1 second if going over 40 mph","2 seconds total","100 feet","Whatever feels safe"]'::jsonb, 0, 'This is the standard rule for heavy vehicles.'),
('doubles_triples', 'Empty trucks:', '["Stop faster than loaded ones","Have better traction","May have poor traction due to bouncing and wheel lockup","Are safer in wind"]'::jsonb, 2, 'Empty trucks have less traction and can bounce stopping.'),
('doubles_triples', 'You should handle a converter dolly with:', '["No care","Ease","Caution, because the center of gravity is high when not connected","Speed"]'::jsonb, 2, 'Dollies can tip over easily when moving them by hand.'),
('doubles_triples', 'With the hand valve on, you should test the trailer brakes by opening the service line valve at the rear of the rig. When you do this, you should hear:', '["Air escaping from the open valve","Nothing","The engine racing","A buzzer"]'::jsonb, 0, 'Air escaping confirms that air is flowing all the way to the back.'),
('doubles_triples', 'Before you can supply air to the air tanks of a second trailer, you need to:', '["Open the shut-off valves at the rear of the first trailer and close the shut-off valves at the rear of the second trailer","Close all valves","Open the emergency line only","Disconnect the air lines"]'::jsonb, 0, 'You must open the valves to let air through to the next unit.'),
('doubles_triples', 'To uncouple the rear trailer, you must typically:', '["Pull the dolly out from under the trailer","Leave the dolly attached","Disconnect the safety chains first","Only lower the landing gear"]'::jsonb, 0, 'After disconnecting and securing, you pull the dolly out.'),
('doubles_triples', 'Converter dollies:', '["Have little or no brakes","Often do not have spring brakes","Have their own engine","Are controlled by the passenger"]'::jsonb, 1, 'Many dollies lack spring brakes, so they can roll if not chocked.'),
('doubles_triples', 'If you must drive through deep water, you should:', '["Speed up","Use gentle braking to keep brakes dry/warm","Disconnect the trailer brakes","Not worry about it"]'::jsonb, 1, 'Light pressure keeps the pads dry.'),
('doubles_triples', 'In a drive-wheel skid:', '["Steer in the opposite direction of the skid","Stop steering","Steer into the skid (in the direction you want to go)","Accelerate"]'::jsonb, 2, 'Steer into the skid to regain control.'),
('doubles_triples', 'Some large trucks have convex or "spot" mirrors. These mirrors:', '["Make things look smaller and farther away than they really are","Make things look larger and closer","Are illegal","Show the area in front of the truck"]'::jsonb, 0, 'Convex mirrors distort distance but show a wider view.'),
('doubles_triples', 'The best way to stop all skids is to:', '["Restore traction to the tires","Brake hard","Steer hard","Accelerate"]'::jsonb, 0, 'No traction means no control.'),
('doubles_triples', 'When backing a double, you should:', '["Back as you would a single","Avoid backing whenever possible","Turn the wheel the opposite way","Back fast"]'::jsonb, 1, 'Backing doubles is extremely difficult; avoid it if you can.'),
('doubles_triples', 'Parking a double or triple trailer combination:', '["Is easier than a single","Requires a large space","Can be done anywhere","Does not require parking brakes"]'::jsonb, 1, 'You need a large parking space that you can pull through.'),
('doubles_triples', 'You are visually checking the coupling of a converter dolly to the rear trailer. How much space should be between the upper and lower fifth wheel?', '["None","1/2 inch","1 inch","2 inches"]'::jsonb, 0, 'There should be no light or space between the plates.'),
('doubles_triples', 'Which of these is NOT a step in uncoupling a converter dolly?', '["Secure the dolly","Disconnect the safety chains","Drive away","Lower the landing gear"]'::jsonb, 2, 'Driving away is the final act, but uncoupling involves securing, disconnecting lines/chains, etc.'),
('doubles_triples', 'Safety chains should be:', '["Loose","Dragging on the ground","Crossed","Secured to the pintle hook"]'::jsonb, 3, 'Chains prevent the dolly from separating if the hitch fails.'),
('doubles_triples', 'When making a turn, the wheels of the rear trailer will:', '["Follow the tractor tracks","Off-track (cheat) more than a single trailer","Stay in the lane","Turn sharply"]'::jsonb, 1, 'Tracking issues are worse with more trailers.'),
('doubles_triples', 'To check if the converter dolly air brakes are working, you should:', '["Open the emergency line shut-off valve at the rear","Listen for air","Drain the tank","Kick the tire"]'::jsonb, 0, 'Opening the valve should cause the brakes to set (if emergency line) or vent (if service line checks).'),
('doubles_triples', 'What is the "shut-off valve"?', '["A valve used to cut off air to the rear trailers","The engine switch","A tire valve","The door handle"]'::jsonb, 0, 'Shut-off valves at the back of each trailer control air flow to the next one.'),
('doubles_triples', 'When connecting the converter dolly to the front trailer, you should lock the pintle hook and:', '["Secure it with the cotter pin or lock","Leave it loose","Weld it","Tie it with rope"]'::jsonb, 0, 'The latch must be secured so it cannot open.'),
('doubles_triples', 'You are driving a 100-foot twin trailer combination at 50 mph. The road is dry and visibility is good. You should keep at least ___ seconds of space ahead of you.', '["5","9","11","15"]'::jsonb, 2, '1 sec per 10ft (10 secs) + 1 sec for speed > 40mph = 11 seconds.'),
('doubles_triples', 'Ideally, you should double check your coupling:', '["By driving fast","By looking at it","By pulling forward gently against the locked brakes","t need to check"]'::jsonb, 2, 'The tug test confirms the coupling holds.'),
('tanks', 'A tank vehicle is used to carry:', '["Any liquid","Any liquid or gas","Bulk liquids or gases","Only water"]'::jsonb, 2, 'A tank vehicle is used to carry any liquid or gaseous materials in a tank.'),
('tanks', 'Which of these is the most important thing to remember about driving a tank vehicle?', '["You must drive faster to keep the liquid from settling","The center of gravity is high","You can stop faster than other vehicles","They are very stable"]'::jsonb, 1, 'High center of gravity makes tankers top-heavy and easy to roll over.'),
('tanks', 'Liquid surge:', '["Is a problem only in bad weather","Is the movement of liquid in a partially filled tank","Helps you stop","Keeps the tank clean"]'::jsonb, 1, 'Movement of liquid (surge) can push the truck in the direction of the wave.'),
('tanks', 'To prevent rollover, you should:', '["Keep the cargo as close to the ground as possible","Drive slowly around turns","Both A and B","Neither A nor B"]'::jsonb, 2, 'Keeping load low (when possible) and driving slow in curves prevents rollovers.'),
('tanks', 'Baffles in liquid cargo tanks do not usually prevent:', '["Side-to-side surge","Front-to-back surge","Heat buildup","Explosions"]'::jsonb, 0, 'Baffles control fore-aft surge but not side-to-side surge.'),
('tanks', 'If your tanker does not have baffles (smooth bore), you should expect:', '["A smoother ride","Strong forward-and-back surge","Less stopping distance","Better handling"]'::jsonb, 1, 'Smooth bore tanks have nothing to slow down the wave of liquid.'),
('tanks', 'You should be extremely cautious when driving smooth bore tankers, especially when you are:', '["Starting or stopping","Driving on highway","Parked","Loading"]'::jsonb, 0, 'Starting and stopping causes the biggest surge in unbaffled tanks.'),
('tanks', 'The amount of liquid to load into a tank depends on:', '["The amount the liquid will expand in transit","The weight of the liquid","Legal weight limits","All of the above"]'::jsonb, 3, 'You must consider expansion, weight density, and legal limits.'),
('tanks', 'Outage means:', '["Driving without lights","Allowance for expansion of liquid","Running out of fuel","A leak"]'::jsonb, 1, 'You must leave room (outage) for liquid to expand as it warms.'),
('tanks', 'A full tank of dense liquid (like some acids) could:', '["Make the truck lighter","Exceed legal weight limits","Lower the center of gravity","Be safer than water"]'::jsonb, 1, 'Dense liquids weigh more; a full tank might be illegal.'),
('tanks', 'If you must make a quick stop to avoid a crash, use:', '["Controlled or stab braking","Only the emergency brake","The hand valve","No brakes"]'::jsonb, 0, 'Use controlled braking to keep the vehicle straight.'),
('tanks', 'Which of these statements about stopping distance and speed is true?', '["You need less stopping distance with a heavy load","Wet roads can double stopping distance","Empty tankers stop the fastest","Speed does not matter"]'::jsonb, 1, 'Slippery roads increase stopping distance significantly.'),
('tanks', 'Empty tanker trucks:', '["Require greater stopping distance than full ones","Stop on a dime","Have excellent traction","Are very stable"]'::jsonb, 0, 'Empty trucks have less traction and bounce, taking longer to stop.'),
('tanks', 'When driving a tank vehicle, you should:', '["Drive fast in curves","Start and stop smoothly","Ignore the surge","Overload the tank"]'::jsonb, 1, 'Smooth operations prevent dangerous surging.'),
('tanks', 'On a tank vehicle, the most likely place for a leak is:', '["The tires","The Discharge valves","The roof","The mirrors"]'::jsonb, 1, 'Valves/manholes are common leak points.'),
('tanks', 'Manhole covers should be:', '["Left open for venting","Closed and secured","Painted red","Loose"]'::jsonb, 1, 'Secure covers prevent leaks and spills.'),
('tanks', 'If you steer a tank vehicle quickly while braking, you:', '["Will stop faster","May cause the vehicle to roll over","Will gain traction","Are driving correctly"]'::jsonb, 1, 'Quick steering + braking + high CG = Rollover.'),
('tanks', 'Side-to-side surge can cause:', '["Rollover","Better gas mileage","Smoother ride","Faster acceleration"]'::jsonb, 0, 'Sloshing side-to-side changes the CG and can tip the truck.'),
('tanks', 'When hauling hazardous materials in a tank, you must:', '["Have a hazardous materials endorsement","Drive only at night","Have a police escort","Wear a suit"]'::jsonb, 0, 'Hazmat load requires H endorsement (usually X for Tank+Hazmat).'),
('tanks', 'Separation of liquids in a multi-compartment tank is done by:', '["Baffles","Bulkheads","Air pressure","Nets"]'::jsonb, 1, 'Bulkheads create separate liquid-tight compartments.'),
('tanks', 'If you drive a tank vehicle that has lost its brakes, use:', '["A tree","A truck escape ramp","Another car","A ditch"]'::jsonb, 1, 'Escape ramps are designed to stop runaways safely.'),
('tanks', 'Portable tanks are:', '["Bulk containers not permanently attached to a truck","Small gas cans","Always empty","Plastic bags"]'::jsonb, 0, 'Portable tanks are loaded onto a flatbed/chassis.'),
('tanks', 'Baffled liquid tanks have bulkheads in them with holes that let the liquid flow through. The headers (bulkheads) help to:', '["Control forward and backward liquid surge","Mix the cargo","Clean the tank","Increase weight"]'::jsonb, 0, 'Baffles slow down the flow fore and aft.'),
('tanks', 'Forward-to-back surge is especially strong in:', '["Smooth bore tanks","Baffled tanks","Small tanks","Square tanks"]'::jsonb, 0, 'Nothing stops the wave in a smooth bore tank.'),
('tanks', 'To control the surge, you should:', '["Keep a steady pressure on the brakes","Brake far in advance and increase pressure gradually","Brake hard at the last minute","Use the hand brake"]'::jsonb, 1, 'Gradual braking prevents a massive wave from hitting the front.'),
('tanks', 'You should know the outage needed for the liquids you carry because:', '["Some liquids expand more than others when they get warm","It is the law","The shipper says so","It saves money"]'::jsonb, 0, 'If you don''t leave enough room, the tank could overflow or burst.'),
('tanks', 'A keyed-in switch for an unloading pump should be:', '["Left on","Turned off when not in use","Always fast","Ignored"]'::jsonb, 1, 'Check unintentional engagement of equipment.'),
('tanks', 'If you have a vehicle with a high center of gravity, you should:', '["Drive fast in curves","Slow down before curves","Turn sharply","Load it to the top"]'::jsonb, 1, 'Slowing down reduces centrifugal force that causes rollovers.'),
('tanks', 'Smooth bore tanks are used for:', '["Milk","Food products","Liquid wishing to be sanitized","All of the above"]'::jsonb, 3, 'Sanitation requires smooth interiors without baffle crevices.'),
('tanks', 'Generally, empty trucks take longer to stop than loaded trucks, but this is NOT true for:', '["Tanks with baffles","Smooth bore tanks","Flatbeds","Vans"]'::jsonb, 0, 'Wait, general rule is empty takes longer. But specifically, a FULL tank stops slower than an EMPTY one? Actually, empty tanks bounce. The wording is tricky. Usually empty = longer stop.'),
('tanks', 'Never load a cargo tank totally full:', '["Because liquid expands","Unless it is water","Because it is too heavy","Unless it is cold"]'::jsonb, 0, 'Always leave outage for expansion.'),
('tanks', 'Which of these is a special requirement for tank vehicles?', '["Low clearance","High center of gravity","Wide turns","Fast acceleration"]'::jsonb, 1, 'Handling the high CG is the primary special skill.'),
('tanks', 'When loading a tank with dividers (bulkheads), you should:', '["Load the front first","Load the back first","Distribute the weight evenly","Fill one to the top then the next"]'::jsonb, 2, 'Don''t overload the axles; balance the load.'),
('tanks', 'You are driving on a clear night. You must dim your headlights from high to low. You should adjust your speed so that you can stop within:', '["The distance you can see ahead","500 feet","The length of your vehicle","100 feet"]'::jsonb, 0, 'Never overdrive your headlights.'),
('tanks', 'Vehicle skids are often caused by:', '["Driving too fast for conditions","Heavy loads","Good tires","Dry roads"]'::jsonb, 0, 'Speed and poor braking cause skids.'),
('tanks', 'If your vehicle is involved in a leak, you should:', '["Drive to a garage","Park it and call for help","Keep driving","Ignore it"]'::jsonb, 1, 'Stop, park, secure the area.'),
('school_bus', 'What is the most important reason for doing a vehicle inspection?', '["To save money","To keep the vehicle looking good","Safety","To pass time"]'::jsonb, 2, 'Safety is the most important reason you inspect your vehicle, ensuring safety for yourself and other road users.'),
('school_bus', 'The absolute best time to conduct the post-trip inspection is:', '["At the start of the next day","After cleaning the bus","Immediately after the last unloading stop","Before going home"]'::jsonb, 2, 'Do it immediately after the last student is unloaded to ensure no one is left behind.'),
('school_bus', 'When approaching a school bus stop, you should activate your overhead amber lights:', '["100 feet before the stop","200 feet before the stop","300 feet before the stop","It depends on the speed limit"]'::jsonb, 1, 'Activate the amber warning lights at least 200 feet or approximately 5-10 seconds before the school bus stop.'),
('school_bus', 'School buses must have at least how many emergency exits?', '["1","2","3","4"]'::jsonb, 3, 'Four emergency exits are required (typically the rear door, two windows, and roof hatch).'),
('school_bus', 'The danger zone for a school bus is the area:', '["10 feet around the bus","In the back of the bus","Under the bus","On the roof"]'::jsonb, 0, 'The danger zone extends 10 feet on all sides of the bus where the driver cannot see.'),
('school_bus', 'If a student drops an object near the bus, they should:', '["Pick it up quickly","Leave it there and move to safety","Ask another student to get it","Crawl under the bus"]'::jsonb, 1, 'Students should leave the object and alert the driver. Picking it up puts them in the danger zone.'),
('school_bus', 'When unloading students at a school, you should tell them to:', '["Run to the building","Walk at least 10 feet away from the bus","Play near the bus","Wait by the tire"]'::jsonb, 1, 'Walking at least 10 feet away ensures they are out of the danger zone.'),
('school_bus', 'Blind spots behind a school bus can extend up to:', '["50 feet","100 feet","200 feet","400 feet"]'::jsonb, 3, 'The area behind the bus is a huge blind spot, extending up to 400 feet.'),
('school_bus', 'After deciding the type of evacuation that is necessary, the next step must be to:', '["Call the principal","Secure the bus","Open the doors","Yell at the students"]'::jsonb, 1, 'Securing the bus (brakes set, engine off, keys removed) prevents it from moving during evacuation.'),
('school_bus', 'Passive highway-rail crossings:', '["Have gates and lights","Do not have any type of traffic control device","Are safer than active crossings","Are rare"]'::jsonb, 1, 'Passive crossings lack active signals like lights or gates; you must look and listen.'),
('school_bus', 'You should adjust all mirrors:', '["While driving","Prior to starting the trip","At the first stop","Only if they look wrong"]'::jsonb, 1, 'Proper mirror adjustment is critical for seeing the danger zones before you move.'),
('school_bus', 'The overhead red warning lights should be activated:', '["When approaching the stop","After coming to a complete stop","After opening the door","Only in emergencies"]'::jsonb, 1, 'Stop first, then open the door/sign to activate red lights.'),
('school_bus', 'During an evacuation, students should be led to a safe place at least:', '["50 feet off the road","100 feet off the road","10 feet off the road","Behind the bus"]'::jsonb, 1, '100 feet is the recommended safe distance from the road/bus in an emergency.'),
('school_bus', 'The golden rule for school bus drivers regarding students who must cross the street is:', '["Hurry them up","Cross all students in the morning only","All students must cross in front of the bus","They can cross behind the bus"]'::jsonb, 2, 'Crossing in front allows the driver to see them.'),
('school_bus', 'A bus driver must stop for a railroad crossing:', '["Between 15 and 50 feet from the nearest rail","On the tracks","5 feet from the tracks","Only if a train is coming"]'::jsonb, 0, '15-50 feet is the legal stopping zone.'),
('school_bus', 'Antilock brakes (ABS):', '["Shorten stopping distance","Help keep the vehicle under control during hard braking","Prevent skids completely","Allow you to drive faster"]'::jsonb, 1, 'ABS prevents wheel lockup, allowing you to steer while braking.'),
('school_bus', 'Before crossing any highway-rail crossing in a school bus, you must:', '["Honk the horn","Flash the lights","Stop, look, and listen","Speed up"]'::jsonb, 2, 'Stop, open the door/window, look, and listen for trains.'),
('school_bus', 'If the gate comes down as you start to cross a railroad highway crossing, you should:', '["Stop immediately","Drive through the gate","Back up","Wait"]'::jsonb, 1, 'Do not stop on the tracks. Drive through the gate if necessary to clear the tracks.'),
('school_bus', 'Recommended procedure for backing a school bus is:', '["Using a spotter","Using mirrors only","Honking","Backing fast"]'::jsonb, 0, 'Backing is dangerous; use a spotter/helper whenever possible.'),
('school_bus', 'When checking the handicap lift, the things you should look for are:', '["Leaking, damaged, or missing parts","Cleanliness only","Color","Speed"]'::jsonb, 0, 'Mechanical integrity is key for the lift.'),
('school_bus', 'The left and right side crossover mirrors are used to see:', '["The tire contact","The danger zone in front of the bus","Behind the bus","Traffic"]'::jsonb, 1, 'Crossover mirrors view the front bumper "danger zone".'),
('school_bus', 'Generally, the safest place in case of a bus fire is:', '["Inside the bus","100 feet away from the bus","Under the bus","On the roof"]'::jsonb, 1, 'Get students off and away (100ft upwind if possible).'),
('school_bus', 'Which of the following will result in a mandatory railroad crossing stop?', '["Empty bus","Students on board","Active crossing","All of the above"]'::jsonb, 3, 'School buses must stop at RR crossings regardless of load (in most states/conditions).'),
('school_bus', 'If you have lost ABS control at one or more wheels:', '["Drive normally, but get the system serviced soon","Information is not available","t work","Stop immediately"]'::jsonb, 0, 'You still have normal brakes, just no anti-lock function.'),
('school_bus', 'By law, school buses must be equipped with a ________ emergency warning device.', '["8-light system","4-light system","Siren","Speaker"]'::jsonb, 0, 'The 8-light system (amber/red) is standard.'),
('school_bus', 'The most dangerous time for students riding the bus is:', '["When riding","Loading and unloading","When sleeping","When eating"]'::jsonb, 1, 'Most accidents happen outside the bus during loading/unloading.'),
('school_bus', 'Usually, if the school bus stalls on a railroad track, what should be done?', '["Try to restart it","Evacuate the students immediately","Push the bus","Call for help inside"]'::jsonb, 1, 'Evacuate immediately if stalled on tracks.'),
('school_bus', 'Which mirror is best for viewing the rear tires?', '["Flat mirror","Convex mirror","Crossover mirror","Rearview mirror"]'::jsonb, 0, 'Flat (West Coast) mirrors give the true view of the sides/tires.'),
('school_bus', 'When evacuation is necessary, it is usually best to use:', '["The front door","The rear door","The window","The door that is safest and most accessible"]'::jsonb, 3, 'Use the exit that provides the safest route away from the hazard.'),
('school_bus', 'Students should be told to leave any dropped objects and move to the point of safety:', '["True","False","Only if expensive","Only if fast"]'::jsonb, 0, 'Correct. Retrieving objects kills kids.'),
('school_bus', 'Tail swing of a school bus can be up to:', '["1 foot","2 feet","3 feet","3 feet or more"]'::jsonb, 3, 'Tail swing can be significant (3ft+) on turns.'),
('school_bus', 'Usually, school bus drivers are not required to stop at:', '["Street car crossings","Railroad tracks used only for industrial switching (where exempt)","Abandoned tracks","Active tracks"]'::jsonb, 1, 'Exempt or abandoned tracks (if marked) may not require a stop.'),
('school_bus', 'The most common bus crash is:', '["Rollover","Intersection collision","Rear-end","Hitting a pedestrian"]'::jsonb, 1, 'Intersection collisions are very common.'),
('school_bus', 'Most states require school bus drivers to take a physical exam:', '["Annually","Every 2 years","Once","Never"]'::jsonb, 0, 'Annual physicals are standard for school bus drivers.'),
('school_bus', 'At a railroad crossing, the bus driver must stop no closer than __ feet from the nearest rail.', '["10","15","20","50"]'::jsonb, 1, '15 feet is the minimum.'),
('school_bus', 'If a student is acting up on the bus, you should:', '["Kick them off","Stand up and yell","Stop safely and handle the situation","Ignore it"]'::jsonb, 2, 'Secure the bus before managing discipline.'),
('school_bus', 'The greatest risk to a bus is:', '["Fire","Other drivers","The driver","Weather"]'::jsonb, 1, 'Other drivers (and their errors) pose the biggest risk.'),
('school_bus', 'When should you use the handrail when entering/exiting?', '["Always","Never","Only when wet","Only when carrying items"]'::jsonb, 0, 'Always use the handrail (3 points of contact).'),
('school_bus', 'A battery box check includes checking for:', '["Securement","Corrosion","Connections","All of the above"]'::jsonb, 3, 'Check for all of these.'),
('school_bus', 'If the bus is on fire, the first thing you should do is:', '["Put it out","Evacuate the students","Call 911","Call the school"]'::jsonb, 1, 'Life safety is #1. Evacuate first.'),
('school_bus', 'Overhead amber lights usually mean:', '["The bus is speeding up","The bus is approaching a stop","Emergency","The bus is broken"]'::jsonb, 1, 'Amber warns traffic that the bus is about to stop.');